INSERT INTO `duan` (`duan_id`, `title`, `description`, `detail`, `is_featured`, `post_time`, `image`, `is_dautu`, `is_giatot`, `is_duanmoi`, `price`, `thongtin`, `vitri`, `tienich`, `matbang`, `tintuc`, `hinhanh`, `vitrihienthi`, `image_alt`, `title_tag`, `meta_description`, `logo`, `text_color`, `footer_color`, `header_color`) VALUES
(1, 'SAIGONMIA TRUNG SƠN - SKY IN YOUR EYES', '<p style="text-align: justify;"><strong><span style="color: #800000;"><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SAI GON MIA TRUNG SƠN</a> </span></strong>hiện hữu tr&ecirc;n khu&ocirc;n đất v&agrave;ng Trung Sơn.L&agrave; nơi được vinh danh của sự gi&agrave;u sang v&agrave; hưởng thụ.Nơi đ&acirc;y Bạn c&oacute; kh&ocirc;ng gian ri&ecirc;ng cua bầu trời,của sự tinh hoa s&ocirc;ng nước thi&ecirc;n cảnh.S&agrave;i G&ograve;n Mia c&aacute;i t&ecirc;n n&oacute;i l&ecirc;n tất cả</p>', '<p><strong><a href="http://hungthinhland.biz/saigonmia-sky-in-your-eyes.html">SaigonMia</a> </strong>tọa lạc tại khu Trung Sơn &ndash; nơi được v&iacute; như &ldquo;b&aacute;n đảo xanh&rdquo; giữa l&ograve;ng th&agrave;nh phố, đ&acirc;y cũng l&agrave; khu vực sầm uất h&agrave;ng đầu, gắn liền với sự ph&aacute;t triển chung của khu đ&ocirc; thị Nam S&agrave;i G&ograve;n. Nhờ c&oacute; 3 mặt view s&ocirc;ng v&agrave; liền kề h&agrave;ng loạt tiện &iacute;ch hiện đại n&ecirc;n <strong>SaigonMia</strong> sẽ đem đến cho gia đ&igrave;nh bạn cuộc sống trong l&agrave;nh v&agrave; đẳng cấp nhất.</p>\r\n<p>&nbsp;</p>\r\n<p align="center"><img src="http://hungthinhland.com/land/attachment/image/saigonMia_pc.jpg" alt="" width="600" height="430" /></p>\r\n<h2><strong>Th&ocirc;ng tin dự &aacute;n:</strong></h2>\r\n<p><strong>Vị tr&iacute;:</strong> Đường 9A, Khu d&acirc;n cư Trung Sơn, TP. HCM<br /><strong>Diện t&iacute;ch khu đất:</strong> 15,126m<sup>2</sup><br /><strong>Mật độ x&acirc;y dựng khối đế:</strong> 46,46%<br /><strong>Mật độ x&acirc;y dựng khối th&aacute;p: </strong>33,70%<br /><strong>Tổng số căn hộ:</strong> 872 căn<br /><strong>Diện t&iacute;ch căn hộ:</strong> 49,83m<sup>2</sup> &ndash; 83,05m <sup>2</sup><br /><strong>Số block:</strong> 03 block<br /><strong>Số tầng:</strong> 27 tầng nổi, 02 tầng hầm<br /><strong>Trung t&acirc;m thương mại:</strong> 04 tầng<br /><strong>Loại h&igrave;nh:</strong> Căn hộ, căn trệt thương mại, office-tel, TTTM</p>\r\n<h2 align="center">TẠI KHU TRUNG SƠN &ndash; NAM S&Agrave;I G&Ograve;N</h2>\r\n<p><strong>SaigonMia</strong> được xem l&agrave; một trong rất &iacute;t dự &aacute;n tại khu Trung Sơn sở hữu vị tr&iacute; hiếm c&oacute; khi tọa lạc tại đường Nguyễn Văn Cừ nối d&agrave;i v&agrave; c&oacute; 3 mặt view s&ocirc;ng. Nhờ sở hữu vị tr&iacute; l&yacute; tưởng n&ecirc;n <strong>SaigonMia</strong> t&aacute;ch biệt vừa đủ để tr&aacute;nh ồn &agrave;o nơi phố thị nhưng vẫn đem lại cho cư d&acirc;n cuộc sống tiện nghi v&agrave; gần gũi thi&ecirc;n nhi&ecirc;n.</p>\r\n<div align="center">\r\n<p>&nbsp;</p>\r\n<p><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/vitri_pc_l.jpg" alt="" width="600" height="434" /></p>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>Từ <strong>SaigonMia</strong>, cư d&acirc;n chỉ mất khoảng 5 ph&uacute;t để v&agrave;o trung t&acirc;m Quận 1 v&agrave; Khu đ&ocirc; thị Ph&uacute; Mỹ Hưng, đồng thời dễ d&agrave;ng tận hưởng những tiện &iacute;ch h&agrave;ng đầu chỉ trong b&aacute;n k&iacute;nh 2km.</p>\r\n<p align="center">&nbsp;<img src="http://www.hungthinhcorp.com.vn/land/attachment/image/text_vt1.png" alt="" width="529" height="91" /></p>\r\n<p>&nbsp;</p>\r\n<h3 align="center">BIỂU TƯỢNG CHO CUỘC SỐNG TIỆN NGHI V&Agrave; ĐẲNG CẤP</h3>\r\n<p>&nbsp;</p>\r\n<p><strong><a href="http://hungthinhland.biz/saigonmia-sky-in-your-eyes.html">SaigonMia</a> </strong>đem đến cuộc sống hiện đại v&agrave; tiện nghi cho c&aacute;c cư d&acirc;n với 04 tầng trung t&acirc;m thương mại &ndash; nơi g&oacute;p mặt của c&aacute;c thương hiệu nổi tiếng thế giới v&agrave; c&aacute;c khu vui chơi giải tr&iacute;. Đặc biệt, 02 hồ bơi tr&agrave;n bờ tr&ecirc;n cao hiện đại v&agrave; độc đ&aacute;o rộng gần 500m<sup>2</sup>, khu c&ocirc;ng vi&ecirc;n trung t&acirc;m, khu sinh hoạt cộng đồng, cafe, gym, spa&hellip; sẽ l&agrave; kh&ocirc;ng gian thư gi&atilde;n tuyệt vời cho bạn v&agrave; những người th&acirc;n y&ecirc;u</p>\r\n<p>&nbsp;</p>\r\n<h2 align="center"><strong>Tiện &iacute;ch nội khu</strong></h2>\r\n<h2 align="center">&nbsp;</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/tisaigon1.jpg" alt="" width="600" height="313" /><br /><em>2 hồ bơi hiện đại v&agrave; độc đ&aacute;o rộng gần 500m2</em></p>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/tisaion2.jpg" alt="C&ocirc;ng vi&ecirc;n kh&ocirc;ng gian thư gi&atilde;n tuyệt vời" width="600" height="314" /><br /><em>C&ocirc;ng vi&ecirc;n kh&ocirc;ng gian thư gi&atilde;n tuyệt vời</em></p>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/tisaion3.jpg" alt="Ph&ograve;ng tập gym n&acirc;ng cao sức khoẻ" width="600" height="313" /><br /><em>Ph&ograve;ng tập gym n&acirc;ng cao sức khoẻ</em></p>\r\n<p align="center"><em><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/tisaion4.jpg" alt="Shopping mall khu mua sắm sang trọng với những thương hiệu nổi tiếng" width="600" height="313" /><br />Shopping mall khu mua sắm sang trọng với những thương hiệu nổi tiếng</em></p>\r\n<p align="center"><strong>Tiện &iacute;ch ngoại khu</strong></p>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/nkmia1.jpg" alt="bệnh viện dịch vụ chăm s&oacute;c sức khỏe h&agrave;ng đầu" width="600" height="313" /><br /><em>Bệnh viện dịch vụ chăm s&oacute;c sức khỏe h&agrave;ng đầu</em></p>\r\n<p align="center"><em><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/nkmia2.jpg" alt="Trung t&acirc;m thương mại sầm uất" width="600" height="313" /><br />Trung t&acirc;m thương mại sầm uất</em></p>\r\n<p align="center"><em><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/nkmia3.jpg" alt="Cộng đồng cư d&acirc;n đẳng cấp" width="600" height="313" /><br />Cộng đồng cư d&acirc;n đẳng cấp</em></p>\r\n<p align="center"><em><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/nkmia4.jpg" alt="Hệ thống trường học danh tiếng" width="600" height="313" /><br />Hệ thống trường học danh tiếng</em></p>\r\n<p align="center"><em><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/nkmia5.jpg" alt="Khu vui chơi giải tr&iacute; nơi gi&uacute;p trẻ ph&aacute;t triển tr&iacute; tuệ" width="600" height="313" /><br />Khu vui chơi giải tr&iacute; nơi gi&uacute;p trẻ ph&aacute;t triển tr&iacute; tuệ</em></p>\r\n<p>&nbsp;</p>\r\n<h2 class="blue mt_40px " align="center">&nbsp;</h2>\r\n<h2 class="blue mt_40px " align="center">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 5)</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/mbmia1.jpg" alt="" width="600" height="389" /></p>\r\n<h2 class="blue mt_40px " align="center">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 6 &ndash; 18)</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/mbmia2.jpg" alt="" width="600" height="380" /></p>\r\n<h2 class="blue mt_40px " align="center">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 19)</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/mbmia3.jpg" alt="" width="600" height="384" /></p>\r\n<h2 class="blue mt_40px " align="center">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 24 &ndash; 25 &ndash; 26 BLOCK C)</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/mbmia4.jpg" alt="" width="600" height="378" /></p>\r\n<h2 class="blue mt_40px " align="center">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 20 &ndash; 21 BLOCK S &ndash; N, TẦNG 20 &ndash; 23 BLOCK C)</h2>\r\n<p align="center"><img src="http://www.hungthinhcorp.com.vn/land/attachment/image/mbmia5.jpg" alt="" width="600" height="381" /></p>\r\n<p>&nbsp;</p>', 1, '2017-01-17 09:05:26', 'saigonmia_pc.jpg', 1, 0, 1, 'Liên Hệ', '<p style="text-align: justify;"><span style="font-size: 12pt;"><span style="color: #800000;"><strong><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaigonMia</a></strong>&nbsp;<span style="color: #000000;">khẳng định th&ecirc;m gi&aacute; trị sống của khu Trung Sơn vốn m&ecirc;nh danh khu nh&agrave; gi&agrave;u của Sai G&ograve;n.Kh&ocirc;ng phải hiển nhi&ecirc;n m&agrave; giới thượng lưu tập trung nơi đ&acirc;y sinh s&ocirc;ng,một miền đất hứa với phong thủy s&ocirc;ng nước bao quanh.Trung Sơn với ba mặt tiền s&ocirc;ng v&agrave; chỉ 1 con đường dẫn v&agrave;o tạo n&ecirc;n sự kh&aacute;c biết cho S&agrave;i G&ograve;n Mia.B&ecirc;n cạnh yếu tố địa h&igrave;nh th&igrave; phần lớn d&acirc;n cư sinh sống ở đ&acirc;y đều giới kinh doanh si&ecirc;u gi&agrave;u c&oacute;,giới tri thức cao,họ sống ở đ&acirc;y v&igrave; đăng cấp,v&igrave; một gia đ&igrave;nh danh gi&aacute;.</span></span>&nbsp;<strong><span style="color: #800000;"><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaigonMia</a>&nbsp;</span></strong><span style="color: #800000;"><span style="color: #000000;">hiện hữu giữa rừng hoa như vậy kh&ocirc;ng thể kem cạnh được,chủ đầu tư muốn SaiGonMia l&agrave; vi&ecirc;n ngọc s&aacute;ng nhất ở Trung sơn v&agrave;&nbsp;</span></span><span style="color: #000000;">sẽ đem đến cho gia đ&igrave;nh bạn cuộc sống trong l&agrave;nh v&agrave; đẳng cấp nhất.</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;">&nbsp;</span></p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/sai-gon-mia.jpg?1470885868294" width="100%" /></a></p>\r\n<p style="text-align: justify;"><span style="font-size: 10pt;"><span style="color: #800000;"><span style="font-size: 12pt;"><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html"><span style="color: #800000;"><strong>S&agrave;i G&ograve;n Mia</strong></span>&nbsp;</a></span>&nbsp;<span style="color: #000000;"><span style="font-size: 12pt;">nằm trong quần thể c&aacute;c kiến tr&uacute;c nh&agrave; phố hiện đại,được thiết kế c&ocirc;ng phu,h&ograve;a chung với lu&ocirc;ng kiến tr&uacute;c đ&oacute;,một kiến tr&uacute;c sang trọng theo thời gian được <span style="color: #800000;"><strong><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaiGonMia</a></strong> </span>tạo n&ecirc;n.</span></span></span><span style="font-size: 12pt;">&nbsp;<span style="color: #000000;">Tọa lạc tại vị tr&iacute; đắc địa nhất của khu vực Nam S&agrave;i G&ograve;n, ngo&agrave;i việc được thừa hưởng những th&agrave;nh quả hạ tầng x&atilde; hội v&agrave; tiện t&iacute;ch&nbsp;sẵn c&oacute; của&nbsp;Mia&nbsp;Trung Sơn hứa hẹn dự &aacute;n sẽ l&agrave; nơi an cư l&yacute; tưởng mọi cư d&acirc;n S&agrave;i G&ograve;n Mia.</span></span></span></p>\r\n<p><span style="font-size: 10pt;">&nbsp;</span></p>\r\n<hr />\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 10pt;">Nếu quan t&acirc;m dự &aacute;n,bạn nhớ để th&ocirc;ng tin tại đ&acirc;y nh&eacute;,Chuy&ecirc;n vi&ecirc;n tư vấn Hưng Thịnh sẽ li&ecirc;n hệ bạn sớm nhất,mọi th&ocirc;ng tin tương t&aacute;c giữa bạn v&agrave; ch&uacute;ng t&ocirc;i đều l&agrave; th&ocirc;ng tin mở.Mọi quyết định l&agrave; ở bạn,ch&uacute;ng t&ocirc;i chỉ cung cấp nhưng th&ocirc;ng tin bạn cần.Tr&acirc;n Trọng Cảm Ơn</span></p>\r\n<h4 style="text-align: center;">&nbsp;</h4>\r\n<h4 style="text-align: center;"><span style="color: #800000; font-size: 14pt;"><strong><span style="color: #ff0000;">ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</span></strong>&nbsp;</span></h4>\r\n<h4 style="text-align: center;"><span style="color: #800000; font-size: 14pt;"><span style="color: #ff0000;"><strong><img src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="43" height="24" /></strong></span></span><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>\r\n<p><span style="font-size: 10pt;"><strong><span style="color: #000000;">S&agrave;i G&ograve;n Mia sở hữu tiện &iacute;ch nội khu vượt trội</span>&nbsp;</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich.png?1470885949851" width="100%" /><img src="../media/images/tien-ich-3.png?1470886467399" width="100%" /><img src="../media/images/tien-ich-4.png?1470886506015" width="100%" /></a></strong><br /><br /></p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 10pt;">Nếu quan t&acirc;m dự &aacute;n,bạn nhớ để th&ocirc;ng tin tại đ&acirc;y nh&eacute;,Chuy&ecirc;n vi&ecirc;n tư vấn Hưng Thịnh sẽ li&ecirc;n hệ bạn sớm nhất,mọi th&ocirc;ng tin tương t&aacute;c giữa bạn v&agrave; ch&uacute;ng t&ocirc;i đều l&agrave; th&ocirc;ng tin mở.Mọi quyết định l&agrave; ở bạn,ch&uacute;ng t&ocirc;i chỉ cung cấp nhưng th&ocirc;ng tin bạn cần.Tr&acirc;n Trọng Cảm Ơn</span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h4 style="text-align: center;"><strong><span style="font-size: 14pt; color: #ff0000;">ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA</span>&nbsp;</strong>&nbsp;</h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="49" height="27" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>\r\n<h4>&nbsp;</h4>\r\n<h4 style="text-align: center;"><a href="#dangkyxemnha">&nbsp;</a></h4>\r\n<p><span style="color: #000000; font-size: 12pt;"><strong>TỔNG QUAN DỰ &Aacute;N</strong></span></p>\r\n<div><span style="color: #000000;">&nbsp;&nbsp;<span style="font-size: 10pt;">&nbsp;<span style="font-size: 12pt;"><strong>Vị tr&iacute;:</strong>&nbsp;Đường 9A, Khu d&acirc;n cư Trung Sơn, TP. HCM&nbsp;</span></span></span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch khu đất:</strong>&nbsp;15.126m<sup>2</sup>&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối đế:</strong>&nbsp;46,46%&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối th&aacute;p:&nbsp;</strong>33,70%&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Tổng số căn hộ:</strong>&nbsp;869 căn&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch căn hộ:</strong>&nbsp;49,83m<sup>2</sup>&nbsp;&ndash; 83,05m&nbsp;<sup>2</sup>&nbsp;</span></div>\r\n<div><span style="color: #000000; font-size: 12pt;">&nbsp;</span></div>\r\n<div><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Số block:</strong>&nbsp;03 block&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Số tầng:</strong>&nbsp;27 tầng nổi, 02 tầng hầm</span></div>\r\n<div><span style="color: #000000; font-size: 12pt;">&nbsp;</span><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Trung t&acirc;m thương mại:</strong>&nbsp;04 tầng&nbsp;</span></div>\r\n<div><br /><span style="color: #000000; font-size: 12pt;">&nbsp;&nbsp;&nbsp;<strong>Loại h&igrave;nh:</strong>&nbsp;Căn hộ, căn trệt thương mại, office-tel, TTTM</span></div>', '<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;"><strong>TẠI KHU TRUNG SƠN - NAM S&Agrave;I G&Ograve;N</strong></span><br /><br /><span style="font-size: 10pt;"><span style="font-size: 12pt;"><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><span style="color: #800000;"><strong>SaigonMia</strong></span>&nbsp;</a></span>&nbsp;<span style="color: #000000; font-size: 12pt;">l&agrave; một trong nhưng dự &aacute;n tầm cao hiện đại bậc nhất tạo lạc tại khu Trung Sơn.Từ con đường Nguyễn Văn Cừ nối d&agrave;i đi v&agrave;o bạn se cảm nhận sự kh&aacute;c biệt của miền đất nơi đ&acirc;y,với ba mặt view s&ocirc;ng&nbsp;.Trong qu&aacute; tr&igrave;nh hội nhập,c&aacute;c th&agrave;nh phố đang qu&aacute; tải về mặt con người,rất kh&oacute; để c&oacute; được một nơi như khu Trung Sơn.Giữa trung t&acirc;m th&agrave;nh thị m&agrave; sở hưu khu đất gần gũi với thi&ecirc;n nhi&ecirc;n như vậy.Nhờ sở hữu vị tr&iacute; l&yacute; tưởng n&ecirc;n </span><span style="font-size: 12pt; color: #800000;"><strong><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaigonMia</a></strong></span>&nbsp;<span style="color: #000000; font-size: 12pt;">t&aacute;ch biệt vừa đủ để tr&aacute;nh ồn &agrave;o nơi phố thị nhưng vẫn đem lại cho cư d&acirc;n cuộc sống tiện nghi v&agrave; gần gũi thi&ecirc;n nhi&ecirc;n.</span></span></p>\r\n<p style="text-align: justify;"><span style="color: #000000;">&nbsp;</span></p>\r\n<div>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/vi-tri.jpg?1470886837178" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p style="text-align: justify;"><br /><span style="font-size: 10pt;"><span style="font-size: 12pt; color: #000000;">Từ</span> <span style="color: #800000;"><span style="font-size: 12pt;"><strong><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaigonMia</a>&nbsp;</strong></span><span style="color: #000000; font-size: 12pt;">bạn c&oacute; thể di chuyển tới trung t&acirc;m th&agrave;nh phố một c&aacute;ch nhanh nhất tr&ecirc;n những con đường xanh m&aacute;t.C</span></span><span style="color: #000000; font-size: 12pt;">ư d&acirc;n chỉ mất khoảng 5 ph&uacute;t để v&agrave;o trung t&acirc;m Quận 1 v&agrave; Khu đ&ocirc; thị Ph&uacute; Mỹ Hưng, đồng thời dễ d&agrave;ng tận hưởng những tiện &iacute;ch h&agrave;ng đầu chỉ trong b&aacute;n k&iacute;nh 2km.</span></span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 10pt;">&nbsp;</span></p>\r\n<p>&nbsp;<img src="../media/images/khoang-cach.png?1470886903603" width="100%" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', '<p><span style="font-size: 12pt; color: #000000;"><strong>BIỂU TƯỢNG CHO CUỘC SỐNG TIỆN NGHI V&Agrave; ĐẲNG CẤP</strong></span></p>\r\n<p style="text-align: justify;"><span style="color: #800000; font-size: 10pt;"><strong><span style="font-size: 12pt; color: #800000;"><a style="color: #800000;" href="../duan-saigonmia-sky-in-your-eyes-1.html">SaigonMia</a></span>&nbsp;</strong><span style="color: #000000; font-size: 12pt;">đảm bảo cho bạn một cuộc sống thưởng lưu với tất cả c&aacute;c dịch vụ hiện đại bậc nhất.Tới nơi đ&acirc;y ban h&ograve;a v&agrave;o sự sang trọng,nhung lụa,nơi tiền bạc kh&ocirc;ng l&agrave; vấn đề,nơi bạn thỏa m&atilde;n đam m&ecirc;,danh vọng sống.Một trung t&acirc;m thương mại quy m&ocirc; 4 tầng với đầy đủ c&aacute;c thương hiệu cao cấp,đồng nghĩa với bạn l&agrave; người sang trọng.SaiGonMia mang trong m&igrave;nh nhưng tiện nghi m&agrave; bất cứ ai trong ch&uacute;ng ta cũng muốn sở hữu như:Trung t&acirc;m thương mại,hồ bơi tr&agrave;n bờ tr&ecirc;n cao,ph&ograve;ng gym,ph&ograve;ng spa,nh&agrave; h&agrave;ng quốc tế,...</span></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><strong>Tiện &Iacute;ch Nội Khu</strong></span></p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-sai-gon-mia-1.jpg?1470887153160" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;"><strong>2 hồ bơi hiện đại v&agrave; độc đ&aacute;o rộng gần 500m2</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-sai-gon-mia-2.jpg?1470887212994" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;"><strong>C&ocirc;ng vi&ecirc;n kh&ocirc;ng gian thư gi&atilde;n tuyệt vời</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;">&nbsp;<span style="color: #ff0000; font-size: 14pt;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</strong>&nbsp;</span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-sai-gon-mia-3.jpg?1470887254401" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;"><strong>Ph&ograve;ng tập gym n&acirc;ng cao sức khoẻ</strong></span></p>\r\n<p><span style="font-size: 10pt;">&nbsp;</span></p>\r\n<p><em><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-sai-gon-mia-4.jpg?1470887287292" width="100%" /></a><br /></em><span style="color: #000000; font-size: 12pt;"><strong>Shopping mall khu mua sắm sang trọng với những thương hiệu nổi tiếng</strong></span></p>\r\n<p><span style="color: #000000; font-size: 12pt;"><strong>Tiện &iacute;ch ngoại khu</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-ngoai-khu-1.jpg?1470887517416" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;"><strong>Bệnh viện dịch vụ chăm s&oacute;c sức khỏe h&agrave;ng đầu</strong></span></p>\r\n<p><span style="font-size: 12pt;">&nbsp;</span></p>\r\n<p><em><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-ngoai-khu-2.jpg?1470887549616" width="100%" /></a><br /></em><span style="color: #000000; font-size: 12pt;"><strong>Trung t&acirc;m thương mại sầm uất</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><em><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-ngoai-khu-3.jpg?1470887581233" width="100%" /></a><br /></em><span style="color: #000000; font-size: 12pt;"><strong>Cộng đồng cư d&acirc;n đẳng cấp</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><em><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-ngoai-khu-4.jpg?1470887615589" width="100%" /></a><br /></em><span style="color: #000000; font-size: 12pt;"><strong>Hệ thống trường học danh tiếng</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><em><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/tien-ich-ngoai-khu-5.jpg?1470887645960" width="100%" /></a><br /></em><span style="color: #000000; font-size: 12pt;"><strong>Khu vui chơi giải tr&iacute; nơi gi&uacute;p trẻ ph&aacute;t triển tr&iacute; tuệ</strong></span></p>\r\n<p><span style="font-size: 12pt;">&nbsp;</span></p>\r\n<h4 style="text-align: center;"><span style="color: #ff0000; font-size: 14pt;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>', '<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mo-hinh-tong-the-sai-gon-mia.png?1470887898064" width="100%" /></a></p>\r\n<h2><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 5)</span></h2>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mat-bang-mia-1.jpg?1470887928471" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h2><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 6 - 18)</span></h2>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mat-bang-mia-2.jpg?1470887986028" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h2><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 19)</span></h2>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mat-bang-mia-3.jpg?1470888026310" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h2><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 24 - 25 - 26 BLOCK C)</span></h2>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mat-bang-mia-4.jpg?1470888057699" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h2><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH (TẦNG 20 - 21 BLOCK S - N, TẦNG 20 - 23 BLOCK C)</span></h2>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/mat-bang-mia-5.jpg?1470888098074" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h4 style="text-align: center;"><span style="color: #ff0000; font-size: 14pt;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', '<p><strong>C&ocirc;ng ty Cổ phần Đầu tư Kinh doanh&nbsp;Địa ốc&nbsp;Hưng Thịnh</strong></p>\r\n<p>Trụ sở ch&iacute;nh: 110 -112 Trần Quốc Toản, Q3, TP.HCM</p>\r\n<p>Ph&ograve;ng giao dịch: Số 45 Ho&agrave;ng Việt, P.4,&nbsp;Q. T&acirc;n B&igrave;nh,&nbsp;TP.HCM</p>\r\n<p>Li&ecirc;n hệ chủ đầu tư: (08)38.788.789 - 0919.889.333 &nbsp;</p>\r\n<p>Email:tuanhoang@hungthinhland.com</p>', '<p><span style="color: #000000; font-size: 12pt;"><strong>Một số h&igrave;nh ảnh thực tế căn hộ mẫu:</strong></span></p>\r\n<p><span style="font-size: 10pt;">&nbsp;</span></p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/can-ho-mau-sai-gon-mia-1.jpg?1470888290857" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/can-ho-mau-sai-gon-mia-2.jpg?1470888322064" width="100%" /></a></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY XEM NH&Agrave; MẪU SAIGON MIA&nbsp;</strong>&nbsp;</span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/can-ho-mau-sai-gon-mia-3.jpg?1470888351302" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/can-ho-mau-sai-gon-mia-4.jpg?1470888392146" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-saigonmia-sky-in-your-eyes-1.html"><img src="../media/images/can-ho-mau-sai-gon-mia-5.jpg?1470888423190" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;</h4>\r\n<hr />\r\n<h4 style="text-align: justify;"><span style="font-size: 10pt;">Nếu quan t&acirc;m dự &aacute;n,bạn nhớ để th&ocirc;ng tin tại đ&acirc;y nh&eacute;,Chuy&ecirc;n vi&ecirc;n tư vấn Hưng Thịnh sẽ li&ecirc;n hệ bạn sớm nhất,mọi th&ocirc;ng tin tương t&aacute;c giữa bạn v&agrave; ch&uacute;ng t&ocirc;i đều l&agrave; th&ocirc;ng tin mở.Mọi quyết định l&agrave; ở bạn,ch&uacute;ng t&ocirc;i chỉ cung cấp nhưng th&ocirc;ng tin bạn cần.Tr&acirc;n Trọng Cảm Ơn</span></h4>\r\n<h4 style="text-align: center;">&nbsp;<span style="color: #ff0000; font-size: 14pt;"><strong>ĐĂNG K&Yacute; NGAY XEM NH&Agrave; MẪU SAIGON MIA&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="203" height="77" /></a></h4>\r\n<h4 style="text-align: center;"><a href="#dangkyxemnha">&nbsp;</a></h4>', 1, 'SKY IN YOUR EYES tại Sài Gòn', 'Dự án SAIGONMIA', 'SKY IN YOUR EYES tại Sài Gòn', 'logo-saigon.jpg', '#ffffff', 'SaiGon Mia', '#804000');
INSERT INTO `duan` (`duan_id`, `title`, `description`, `detail`, `is_featured`, `post_time`, `image`, `is_dautu`, `is_giatot`, `is_duanmoi`, `price`, `thongtin`, `vitri`, `tienich`, `matbang`, `tintuc`, `hinhanh`, `vitrihienthi`, `image_alt`, `title_tag`, `meta_description`, `logo`, `text_color`, `footer_color`, `header_color`) VALUES
(2, 'CĂN HỘ 9VIEW HƯNG THỊNH QUẬN 9', '<p style="text-align: justify;"><strong><span style="color: #800000;">CĂN HỘ 9 VIEW</span></strong> tọa lạc tại đường tăng nhơn ph&uacute;,quận 9.Nơi đ&acirc;y sự ph&aacute;t tri&ecirc;n ng&agrave;y cang mạnh mẽ vơi sự hiện diện của c&aacute;c &ocirc;ng lớn ng&agrave;nh thương mại điện tử.Với sự ưu đ&atilde;i về gi&aacute; cả song 9 view mang lại cho c&aacute;c gia đ&igrave;nh trẻ đầy đủ tiền nghi trong cuộc sống tấp nập cua Sai Gon hoa lệ.&nbsp;</p>', '<p><strong>880 triệu/căn</strong></p>\r\n<p><strong>Chiết khấu 4% &ndash; 24% từ Hưng Thịnh Land</strong></p>\r\n<p>Với &yacute; tưởng mang đến cho c&aacute;c gia đ&igrave;nh trẻ một kh&ocirc;ng gian sống xanh, h&agrave;i ho&agrave;&nbsp; v&agrave; c&acirc;n bằng cuộc sống với thi&ecirc;n nhi&ecirc;n, Hưng Thịnh đ&atilde; mang đến Dự &Aacute;n <a href="http://hungthinhland.biz/can-ho-9view-hung-thinh-quan-9.html">Căn Hộ&nbsp;9 View</a> với mức gi&aacute; v&ocirc; c&ugrave;ng hấp dẫn.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/9vew%20_%20hinh%20tieu%20de_sua.jpg" alt="Can ho 9 view" /></p>\r\n<p>&nbsp;</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Phoi%20canh%20can-ho-9-view_sua.jpg" alt="Căn hộ 9 view" /></p>\r\n<p><strong>Video clip phối cảnh tổng thể dự &aacute;n Căn hộ 9view</strong></p>\r\n<p><iframe src="https://www.youtube.com/embed/H0bbzff-CX0" name="Clip Căn hộ 9view" width="380" height="280" frameborder="0" scrolling="no" align="middle" allowfullscreen="allowfullscreen" data-mce-fragment="1"></iframe></p>\r\n<p><strong>&nbsp;1)&nbsp;&nbsp;</strong><strong>TỔNG QUAN DỰ &Aacute;N</strong></p>\r\n<p>T&ecirc;n Dự &Aacute;n: 9 View Apartment (Căn Hộ 9 View Quận 9)</p>\r\n<p>Chủ Đầu Tư: Hưng Thịnh Corp</p>\r\n<p>Đơn Vị Ph&acirc;n Phối: Hưng Thịnh Land</p>\r\n<p>Đơn Vị Thi C&ocirc;ng: Hưng Thịnh Construction</p>\r\n<p>Vị Tr&iacute;: Phước Long B, Quận 9, Tp. HCM</p>\r\n<p>Diện T&iacute;ch Dự &Aacute;n: 10.000m2</p>\r\n<p>Diện T&iacute;ch X&acirc;y Dựng: 12.627 m2</p>\r\n<p>Mật Độ X&acirc;y Dựng: 40%</p>\r\n<p>Số Block: 03</p>\r\n<p>Số Tầng: 18</p>\r\n<p>Diện T&iacute;ch Căn Hộ: 58.1 m2 &ndash; 86.98 m2</p>\r\n<p>Tầng Hầm: 01</p>\r\n<p>Tầng Thương Mại: 01</p>\r\n<p>Tầng Căn Hộ: 17</p>\r\n<p><strong>2) VỊ TR&Iacute; CĂN HỘ 9 VIEW :</strong></p>\r\n<p>Dự &aacute;n <strong>Căn Hộ 9View</strong> sở hữu một vị tr&iacute; ngay tại trung t&acirc;m quận 9 nằm trong khu quy hoạch đồng bộ v&agrave; ho&agrave;n chỉnh tạo n&ecirc;n cho căn hộ 9View Apartment c&oacute; vị tr&iacute; v&ocirc; c&ugrave;ng đắc địa v&agrave; tiềm năng, nằm liền kề khu d&acirc;n cư sầm uất v&agrave; l&acirc;u đời, nằm cạnh trường Cao Đẳng C&ocirc;ng Thương Tp.HCM, đối diện với dự &aacute;n l&agrave; khu phức hợp Sping Town Phước Long, dự &aacute;n chỉ c&aacute;ch ga B&igrave;nh Th&aacute;i của t&agrave;u điện ngầm Metro 500m, c&aacute;ch Metro Quận 2 &ndash; CoopMart Quận 9 chỉ 5 ph&uacute;t duy chuyển.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/vi%20tri%20can%20ho%209%20view.jpg" alt="vị tr&iacute; can ho 9view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/vi-tri%20d%E1%BB%B1%20%C3%A1n%209view_sua(1).jpg" alt="VỊ tr&iacute; dự &aacute;n căn hộ 9 view" /></p>\r\n<p>Dự &Aacute;n 9view Apartment Quận 9 c&oacute; vị tr&iacute; giao th&ocirc;ng v&ocirc; c&ugrave;ng thuận lợi, từ dự &aacute;n qu&yacute; kh&aacute;ch c&oacute; thể dễ d&agrave;ng di chuyển đi c&aacute;c Quận l&acirc;n cận bằng c&aacute;c tuyến đường huyết mạch của th&agrave;nh phố như: đi Thủ Đức chỉ mất 2km, đi Quận 2 chỉ mất 3km, đi B&igrave;nh Thạnh bằng Xa Lộ H&agrave; Nội chỉ 4km&hellip; Đặt biệt căn hộ 9 View Apartment kết nối trực tiếp với cảng c&aacute;t l&aacute;i đ&acirc;y l&agrave; cơ hội lớn cho những qu&yacute; kh&aacute;ch h&agrave;ng đang c&ocirc;ng t&aacute;c v&agrave; l&agrave;m việc tại Cảng C&aacute;t L&aacute;i v&agrave; hơn thế nữa từ dự &aacute;n qu&yacute; kh&aacute;ch chỉ mất từ 20 &ndash; 30 ph&uacute;t để đến s&acirc;n bay T&acirc;n Sơn Nhất với Đại Lộ Phạm Văn Đồng.</p>\r\n<p><strong>&nbsp;3)&nbsp;&nbsp;</strong><strong>TIỆN &Iacute;CH CỦA KHU <a href="http://hungthinhland.biz/can-ho-9view-hung-thinh-quan-9.html">CĂN HỘ 9VIEW </a>:</strong></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/tien-ich-noi-khu-can-ho-9-view(1).JPG" alt="tiện &iacute;ch nội khu tại dự &aacute;n căn hộ 9 view" /><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%209%20view_v%C6%B0%E1%BB%9Dn%20yoga_sua.jpg" alt="tiện &iacute;ch tại dự &aacute;n căn hộ 9 view" /></p>\r\n<p><strong>TIỆN &Iacute;CH NỘI KHU :</strong></p>\r\n<p><strong><em>Tiện &iacute;ch tại căn hộ 9 View đa dạng &amp; vượt trội</em></strong></p>\r\n<p><strong><em><img src="http://sanhungthinh.com.vn/upload/images/can%20ho%209%20view_cong%20vien%20noi%20khu.jpg" alt="c&ocirc;ng vi&ecirc;n nội khu tai dự &aacute;n 9 view" /></em></strong></p>\r\n<ol>\r\n<li>Khu tr&ograve; chơi cho trẻ: Gi&uacute;p b&eacute; thỏa sức kh&aacute;m ph&aacute; thế giời xung quanh m&igrave;nh, r&egrave;n luyện thể lực v&agrave; ph&aacute;t triển tr&iacute; tuệ.</li>\r\n<li>Vườn thảm cỏ: Nơi c&aacute;c cư d&acirc;n c&oacute; thể tập yoga hay tổ chức c&aacute;c hoạt động ngo&agrave;i trời.</li>\r\n<li>Khu vườn thư gi&atilde;n: L&agrave; nơi cư d&acirc;n 9VIEW h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n, tản bộ thư gi&atilde;n sau ng&agrave;y l&agrave;m việc bận rộn.</li>\r\n<li>S&acirc;n tập dưỡng sinh: d&agrave;nh cho người cao tuổi được đặc biệt ch&uacute; trọng tại 9VIEW.</li>\r\n<li>Khu phức hợp thể thao cầu l&ocirc;ng &ndash; b&oacute;ng rổ &ndash; b&oacute;ng chuyền: l&agrave; nơi l&yacute; tưởng cho c&aacute;c bạn trẻ năng động.</li>\r\n<li>Vườn BBQ ngo&agrave;i trời: l&agrave; nơi c&aacute;c gia đ&igrave;nh tổ chức c&aacute;c buổi tiệc nướng cuối tuần vui vẻ c&ugrave;ng bạn b&egrave;.</li>\r\n</ol>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C3%B4ng%20vi%C3%AAn%20ch%E1%BB%A9c%20n%C4%83ng%20t%E1%BA%A1i%209%20view.JPG" alt="c&ocirc;ng vi&ecirc;n chức năng tại 9 view" /></p>\r\n<p><strong>Khu c&ocirc;ng vi&ecirc;n chức năng rộng 6000m2 l&agrave; điểm nổi bật của dự &aacute;n căn hộ 9 view</strong></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%209%20view_v%C6%B0%E1%BB%9Dn%20yoga.jpg" alt="vườn yoga tại dự &aacute;n 9 view" /></p>\r\n<p><strong>Vườn Yoga tại 9 View</strong></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%209view_khu%20the%20thao.jpg" alt="Khu thể thao tại dự &aacute;n 9 view" /></p>\r\n<p><strong>Khu thể thao trong dự &aacute;n Căn hộ 9 view</strong></p>\r\n<p><strong><img src="http://sanhungthinh.com.vn/upload/images/can%20ho%209%20view_%20vuon%20nuong.jpg" alt="Vườn nướng tại dự &aacute;n căn hộ 9 view" /></strong></p>\r\n<p><strong>TIỆN &Iacute;CH NGOẠI KHU :</strong></p>\r\n<p><strong><em>Lợi thế của dự &aacute;n 9View Apartment ch&iacute;nh l&agrave; nằm trong khu vực được quy hoạch ho&agrave;n thiện v&agrave; đồng bộ cả về nội khu dự &aacute;n v&agrave; c&aacute;c v&ugrave;ng l&acirc;n cận</em></strong></p>\r\n<p>&bull; &nbsp;Khu li&ecirc;n hợp thể dục thể thao Rạch Chiếc rộng 466ha đạt chuẩn Olympic, &nbsp;Trung t&acirc;m văn ho&aacute; Q.9, s&acirc;n Golf Rạch Chiếc</p>\r\n<p>&bull; &nbsp;Trục đường Xa lộ H&agrave; Nội kết nối trực tiếp dự &aacute;n đến trung t&acirc;m Q1 v&agrave; trung t&acirc;m h&agrave;nh ch&iacute;nh Q2</p>\r\n<p>&bull; &nbsp;Tuyến t&agrave;u điện ngầm Metro c&aacute;ch dự &aacute;n chỉ 5 ph&uacute;t</p>\r\n<p>&bull; &nbsp;Chỉ mất 1h30 ph&uacute;t để tới th&agrave;nh phố biển Vũng T&agrave;u qua Cao tốc Long Th&agrave;nh &ndash; Dầu Gi&acirc;y.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/ti%E1%BB%87n%20%C3%ADch%20xunh%20quanh%20c%C4%83n%20h%E1%BB%99%209%20view.png" alt="tiện &iacute;ch xunh quanh căn hộ 9 view" /></p>\r\n<p><strong>Trường học (1 ph&uacute;t&nbsp; &ndash; 15 ph&uacute;t) :</strong></p>\r\n<p>Trường mẫu gi&aacute;o 3 tầng tại Khu đ&ocirc; thị</p>\r\n<p>Trường Cao đẳng C&ocirc;ng thương</p>\r\n<p>Trường THPT Phước Long</p>\r\n<p>Trường THCS Phước B&igrave;nh</p>\r\n<p>Trường tiểu học Phong Ph&uacute;</p>\r\n<p>Trường CĐ nghề Kỹ thuật c&ocirc;ng nghệ TPHCM</p>\r\n<p>Trường ĐH kỹ thuật</p>\r\n<p>Trường ĐH GTVT</p>\r\n<p><strong>Si&ecirc;u thị v&agrave; TTTM (10 &ndash; 15 ph&uacute;t)</strong></p>\r\n<p>Co.op Mart Xa Lộ H&agrave; Nội</p>\r\n<p>BigC Caltavil,</p>\r\n<p>Metro</p>\r\n<p>VinCom Thủ Đức, Vincom Megamal</p>\r\n<p><strong>Bệnh viện (10 &ndash; 15 ph&uacute;t):</strong></p>\r\n<p>Bệnh viện Qu&acirc;n y miền Đ&ocirc;ng,</p>\r\n<p>Bệnh viện Quận 9</p>\r\n<p>Trạm y tế Phước Long B</p>\r\n<p><strong>Trung t&acirc;m h&agrave;nh ch&iacute;nh (2 ph&uacute;t)</strong></p>\r\n<p>Ủy ban nh&acirc;n d&acirc;n phường Phước Long B</p>\r\n<p>C&ocirc;ng an phường Phước Long B</p>\r\n<p><strong>4)&nbsp;&nbsp; </strong><strong>MẶT BẰNG V&Agrave; THIẾT KẾ CĂN HỘ TẠI DỰ &Aacute;N 9 VIEW :</strong></p>\r\n<p>Căn hộ 9 View Tăng Nhơn Ph&uacute; c&oacute; thiết kế kiến tr&uacute;c th&ocirc;ng minh v&agrave; h&agrave;i h&ograve;a, với diện t&iacute;ch căn hộ vừa v&agrave; nhỏ từ 58m2 đến 86m2 ph&ugrave; hợp với nhu cầu của đại đa số gia đ&igrave;nh trẻ tại S&agrave;i G&ograve;n. Y&ecirc;n vị trong căn hộ của m&igrave;nh tại khu Căn hộ 9view bạn sẽ lạc v&agrave;o một thế giới b&igrave;nh y&ecirc;n.</p>\r\n<p>&nbsp;</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Mat%20bang%20can%20ho%209%20view_sua.jpg" alt="Mặt bằng căn hộ 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/m%E1%BA%B7t%20c%C3%A1t%20c%C4%83n%20h%E1%BB%99%209%20view%201.jpg" alt="mat bang tang can ho 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/m%E1%BA%B7t%20c%C3%A1t%20c%C4%83n%20h%E1%BB%99%209%20view%202.jpg" alt="mat bang tang dien hinh can ho 9 view" /></p>\r\n<p><strong>Mẫu thiết kế căn hộ tại dự &aacute;n 9View</strong></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%2058_1m2_9%20view.jpg" alt="Căn hộ 2 ph&ograve;ng ngủ C14_dự &aacute;n 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%20C15_%20d%E1%BB%B1%20%C3%A1n%209%20view.jpg" alt="Căn hộ C15_dự &aacute;n 9view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%20B15_%20d%E1%BB%B1%20%C3%A1n%209%20view.jpg" alt="Căn hộ B15 tại dự &aacute;n 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/c%C4%83n%20h%E1%BB%99%20A6_9view.jpg" alt="Căn hộ A6_ dự &aacute;n 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%20A2%2086%2C9m2_d%E1%BB%B1%20%C3%A1n%20c%C4%83n%20h%E1%BB%99%20%209%20view.jpg" alt="" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%2081m2%20c%C4%83n%20A5.jpg" alt="Căn hộ A5_dự &aacute;n 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/C%C4%83n%20h%E1%BB%99%203%20ph%C3%B2ng%20ng%E1%BB%A7%20B10_d%E1%BB%B1%20%C3%A1n%209%20view.jpg" alt="Căn hộ B10_ dự &aacute;n 9 view" /></p>\r\n<p><strong>5)&nbsp;&nbsp; </strong><strong>NHỮNG ƯU ĐIỂM CỦA CĂN HỘ 9 VIEW :</strong></p>\r\n<p><strong><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ĐIỂM MẠNH CỦA CĂN HỘ 9 VIEW QUẬN 9</em></strong></p>\r\n<p>&bull; Diện t&iacute;ch nhỏ nhưng thiết kế th&ocirc;ng minh với 2 ph&ograve;ng ngủ, 2 toilet, tất cả c&aacute;c ph&ograve;ng đều c&oacute; cửa sổ th&ocirc;ng tho&aacute;ng.</p>\r\n<p>&bull; Liền kề c&aacute;c tiện &iacute;ch của khu phức hợp: nh&agrave; giữ trẻ 3 tầng, Trung t&acirc;m thương mai 6 tầng.</p>\r\n<p>&bull; C&aacute;c tiện &iacute;ch kh&aacute;c như: trường học, si&ecirc;u thị, bệnh viện, khu c&ocirc;ng nghệ cao&hellip;</p>\r\n<p>&bull; Với cộng đồng d&acirc;n cư văn minh, trẻ trung, hiện đại kết hợp c&ugrave;ng kh&ocirc;ng gian y&ecirc;n tĩnh, m&ocirc;i trường sống trong l&agrave;nh, tiện nghi bạn sẽ ho&agrave;n to&agrave;n an t&acirc;m v&agrave;o sự lựa chọn của m&igrave;nh.</p>\r\n<p>&bull; Đường nội khu được thiết kế s&acirc;n vườn đường dạo.</p>\r\n<p>&bull; C&ocirc;ng vi&ecirc;n với 6 khu chức năng đa dạng, đa sắc m&agrave;u ph&ugrave; hợp với mọi đối tượng, g&oacute;p phần mang đến sự tiện nghi v&agrave; gi&uacute;p cư d&acirc;n dễ d&agrave;ng chăm s&oacute;c sức khỏe mỗi ng&agrave;y.</p>\r\n<p>&bull; Kh&ocirc;ng gian y&ecirc;n tĩnh, trong l&agrave;nh.</p>\r\n<p>&bull; Sở hữu mức gi&aacute; v&ocirc; c&ugrave;ng bất ngờ, ch&iacute;nh s&aacute;ch thanh to&aacute;n của Căn hộ 9 View được thiết kế d&agrave;nh cho c&aacute;c bạn trẻ c&oacute; thu nhập ổn định.</p>\r\n<p><strong>6) H&Igrave;NH ẢNH NH&Agrave; MẪU TẠI DỰ &Aacute;N CĂN HỘ 9 VIEW :</strong></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/ph%C3%B2ng%20kh%C3%A1ch%20c%C4%83n%20h%E1%BB%99%209%20view%20qu%E1%BA%ADn%209.jpg" alt="ph&ograve;ng kh&aacute;ch căn hộ mẫu dự &aacute;n 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/nh%C3%A0%20m%E1%BA%ABu%20c%C4%83n%20h%E1%BB%99%209%20view%20qu%E1%BA%ADn%209.jpg" alt="nh&agrave; mẫu căn hộ 9 view" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/ph%C3%B2ng%20ng%E1%BB%A7%20c%C4%83n%20h%E1%BB%99%209%20view%20qu%E1%BA%ADn%209.jpg" alt="ph&ograve;ng ngủ căn hộ mẫu 9 view" /></p>\r\n<p><strong>H&igrave;nh ảnh nh&agrave; mẫu căn hộ 9 view</strong></p>\r\n<p><strong>&nbsp;7) CH&Iacute;NH S&Aacute;CH B&Aacute;N H&Agrave;NG TỪ HƯNG THỊNH :</strong></p>\r\n<p><strong>&nbsp; &nbsp;Ưu đ&atilde;i hấp dẫn cho những kh&aacute;ch h&agrave;ng đầu ti&ecirc;n</strong></p>\r\n<ul>\r\n<li>Kh&aacute;ch h&agrave;ng đăng k&yacute; giữ chổ đầu ti&ecirc;n được nhận chiết khấu 0.5</li>\r\n<li>Chiết khấu 4% trong đợt mở b&aacute;n đầu ti&ecirc;n.</li>\r\n<li>Chiết khấu 4- 9% cho kh&aacute;ch h&agrave;ng thanh to&aacute;n nhanh.</li>\r\n</ul>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/can%20ho%209%20view_pannel%20mo%20ban.jpg" alt="gi&aacute; b&aacute;n căn hộ 9 view" /></p>\r\n<p><strong>8) THANH TO&Aacute;N CĂN HỘ 9 VIEW QUẬN 9</strong></p>\r\n<p>&ndash; CĐT Hưng Thịnh cam kết mức gi&aacute; ưu đ&atilde;i v&agrave; hấp dẫn nhất cho những kh&aacute;ch h&agrave;ng đầu ti&ecirc;n đăng k&yacute; nhận đặt chỗ căn hộ 9 view trước 15/01/2016.</p>\r\n<p>&ndash; <a href="http://hungthinhland.biz/can-ho-9view-hung-thinh-quan-9.html">CĂN HỘ 9 VIEW</a>&nbsp; b&agrave;n giao ho&agrave;n thiện. Tặng nội thất cao cấp.</p>\r\n<p>&ndash; Chương tr&igrave;nh thanh to&aacute;n ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng giao dịch th&agrave;nh c&ocirc;ng trong đợt mở b&aacute;n đầu ti&ecirc;n ( K&yacute; hợp đồng chỉ từ 10%-12%). C&aacute;c đợt tiếp theo thanh to&aacute;n theo tiến độ.</p>\r\n<p>&ndash; Với Phương Thức Thanh To&aacute;n theo tiến độ x&acirc;y dựng, chia th&agrave;nh nhiều đợt ( mỗi đợt 5% ) đến l&uacute;c bạn nhận nh&agrave;, căn hộ 9 view v&ocirc; c&ugrave;ng ph&ugrave; hợp cho việc mua nh&agrave; theo h&igrave;nh thức trả g&oacute;p kh&ocirc;ng l&atilde;i suất đặc biệt d&agrave;nh cho những cặp vợ chồng trẻ hoặc c&aacute;c nh&agrave; đầu tư.</p>\r\n<p>&ndash; Ng&acirc;n h&agrave;ng hỗ trợ&nbsp; 70% trong v&ograve;ng 20 năm, v&agrave; mỗi th&aacute;ng bạn chỉ cần d&agrave;nh dụm một số tiền trung b&igrave;nh khoảng 5-8 triệu/th&aacute;ng/ 2 vợ chồng, l&agrave; bạn c&oacute; thể căn hộ 9 view&nbsp; với kh&ocirc;ng gian sống xanh Nơi bạn v&agrave; gia đ&igrave;nh vun đắp hạnh ph&uacute;c d&agrave;i l&acirc;u.</p>\r\n<p>&ndash; Với lợi thế về vị tr&iacute;, gi&aacute; th&agrave;nh, phương thức thanh to&aacute;n với việc đầu tư v&agrave;o dự &aacute;n can ho 9 view l&agrave; một cơ hội v&ocirc; c&ugrave;ng thuận lời v&igrave; mật độ d&acirc;n số ở khu vực kh&aacute; đồng. Liền kề c&aacute;c quận trung t&acirc;m v&agrave; đặc biệt l&agrave; khu c&ocirc;ng nghệ cao th&agrave;nh phố n&ecirc;n nhu cầu thu&ecirc; nh&agrave; ở đ&acirc;y l&agrave; v&ocirc; c&ugrave;ng lớn.</p>', 1, '2016-12-15 02:38:53', 'phoi_canh_1_1.jpg', 0, 1, 1, '1.1 tỷ', '<p><span style="font-size: 12pt; color: #000000;"><strong>Vị tr&iacute;:</strong>&nbsp;<strong>Số&nbsp;18 Tăng Nhơn Ph&uacute;, Phường Phước Long B, Quận 9&nbsp;</strong></span></p>\r\n<p><br /><span style="font-size: 12pt;"><strong><span style="color: #800000;">&nbsp;&nbsp;&nbsp; <span style="color: #000000;">Diện t&iacute;ch khu đất</span></span></strong><span style="color: #000000;">: 12.627m2</span></span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;&nbsp; <strong>Mật độ x&acirc;y dựng</strong>: 40%</span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;&nbsp;<strong> Số block</strong>: 03 block</span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;&nbsp; <strong>Tầng hầm</strong>: 01 tầng</span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;&nbsp;<strong> Tầng cao</strong>: 18 tầng</span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;<strong>&nbsp; Tổng số căn hộ</strong>: 690 căn</span><br /><span style="font-size: 12pt; color: #000000;">&nbsp;&nbsp;&nbsp; <strong>Diện t&iacute;ch căn hộ</strong>: 58,1m2 - 86,98</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Đến với</span> <span style="font-size: 12pt;"><strong><span style="color: #800000;"><a style="color: #800000;" href="../duan-can-ho-9view-hung-thinh-quan-9-2.html">Căn Hộ 9 VIEW</a></span></strong> </span><span style="color: #000000; font-size: 12pt;">bạn t&acirc;n hương dược sự y&ecirc;n binh của cư d&acirc;n nơi đ&acirc;y.Một dự &aacute;n th&iacute;ch hợp cho c&aacute;c bạn trẻ muốn an cư lập nghiệp ở TPHCM,với một mức gi&aacute; rất t&ocirc;t.Dự &aacute;n c&oacute; đầy đủ tiện &iacute;ch cho gia đ&igrave;nh bạn,b&ecirc;n cạnh đ&oacute; vị tr&iacute; cũng thuận lợi cho cuộc s&ocirc;ng của bạn.</span></p>\r\n<div>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tong-quan-9-view.jpg?1470901473344" width="100%" /></a></p>\r\n</div>\r\n<p style="text-align: justify;"><span style="color: #800000; font-size: 12pt;"><a style="color: #800000;" href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><strong>Căn hộ 9 view</strong></a></span> <span style="color: #000000; font-size: 12pt;">tọa lạc tr&ecirc;n&nbsp;đường&nbsp;Tăng Nhơn Ph&uacute;, Phường Phước Long B, Quận 9, cư d&acirc;n nơi đ&acirc;y thoải m&aacute;i di chuyển với 2 mặt tiền đường nội khu rộng,&nbsp;c&aacute;ch trung t&acirc;m TPHCM chưa tới 5km, gắn li&ecirc;n&nbsp;c&aacute;c trục đường lớn&nbsp;giao th&ocirc;ng thuận lợi: Xa Lộ H&agrave; Nội, Đại Lộ Mai Ch&iacute; Thọ với&nbsp;2 trục đường xuy&ecirc;n suốt&nbsp;L&ecirc; Văn Việt v&agrave;&nbsp;Đỗ Xu&acirc;n Hợp kết nối mọi nơi cho cư d&acirc;n thuận tiện di chuyển nơi đ&acirc;u, đặc biệt&nbsp;c&aacute;c quận trung t&acirc;m như&nbsp;Q1,Q2, B&igrave;nh Thạnh,Q9 kh&ocirc;ng chỉ vậy vị tr&iacute; c&ograve;n thuận tiện cho cư d&acirc;n muốn di chuyển ra c&aacute;c tỉnh&nbsp;B&igrave;nh Dương, Đồng Nai chỉ trong 15 ph&uacute;t.</span></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/vi-tri-du-an-9-view.png?1470901516616" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 9 VIEW</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Khu&ocirc;n vi&ecirc;n nội khu rộng với tổng diện t&iacute;ch 12.627 m2&nbsp;nhưng mật độ x&acirc;y dựng chỉ 40% ngo&agrave;i ra l&agrave;&nbsp;c&aacute;c khu vui chơi trẻ em, c&ocirc;ng vi&ecirc;n,&nbsp;s&acirc;n tập thể dục v&agrave; được che phủ bởi c&acirc;y xanh tạo n&ecirc;n một kh&ocirc;ng gian sống chất lượng cao,&nbsp;tr&agrave;n ngập m&agrave;u xanh. Căn hộ được chia th&agrave;nh 3 Block (A,B,C) với 18&nbsp;tầng trong đ&oacute;:&nbsp;1 tầng hầm để xe, 2 tầng thương mại v&agrave;&nbsp;từ tầng 3-&gt;17 l&agrave; tầng căn hộ&nbsp;với tổng 690 căn hộ sở hữu nhiều diện t&iacute;ch kh&aacute;c nhau từ 58-&gt;87 m2 (2PN - 3PN)</span>,&nbsp;<span style="color: #800000; font-size: 12pt;"><a style="color: #800000;" href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><strong>9 View</strong></a></span>&nbsp;<span style="color: #000000; font-size: 12pt;">hứa hẹn sẽ l&agrave; một dự &aacute;n h&agrave;i l&ograve;ng cho những cư d&acirc;n kh&oacute; t&iacute;nh nhất</span></p>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>TIỆN &Iacute;CH DỰ &Aacute;N VƯỢT TRỘI</strong></span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">L&agrave; một dự &aacute;n&nbsp;kiểu mẫu hiện đại v&agrave; cao cấp, được quy hoạch đồng bộ v&agrave; ho&agrave;n chỉnh trong khu d&acirc;n cư hiện hữu sẵn c&oacute; c&aacute;c tiện &iacute;ch về gi&aacute;o dục, y tế, thương mại, ng&acirc;n h&agrave;ng, kh&ocirc;ng những thế dự &aacute;n&nbsp;9 View c&ograve;n sở hữu một&nbsp;hệ thống tiện &iacute;ch nội khu nổi bật như:</span></p>\r\n<ul>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>Khu vui chơi trẻ em</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>Vườn yoga, vườn thư giản</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>Khu thể dục dụng cụ&nbsp;</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>S&acirc;n cầu l&ocirc;ng&nbsp;</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>B&oacute;ng rổ</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>B&oacute;ng chuyển&nbsp;</strong></span></li>\r\n<li><span style="font-size: 12pt; color: #000000;"><strong>Khu vườn BBQ ngo&agrave;i trời.</strong>&nbsp;</span></li>\r\n</ul>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-9-view.jpg?1470901552727" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><iframe src="https://www.youtube.com/embed/1UHqoBoanwQ" width="70%" height="350" frameborder="0" scrolling="no" data-mce-fragment="1"></iframe></p>\r\n<h4 style="text-align: center;">&nbsp;<a href="#dangkyxemnha"><br /></a></h4>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 9 VIEW</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', '<p><span style="color: #800000; font-size: 12pt;"><a style="color: #800000;" href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><strong>VỊ TR&Iacute; DỰ &Aacute;N&nbsp;9 VIEW</strong></a></span></p>\r\n<p style="text-align: justify;"><span style="color: #800000; font-size: 12pt;"><strong>Căn hộ 9 view</strong></span>&nbsp;<span style="color: #000000; font-size: 12pt;">nằm tr&ecirc;n&nbsp;đường&nbsp;Tăng Nhơn Ph&uacute;, Phường Phước Long B, Quận 9, tọa lạc vị tr&iacute; trắc địa với 2 mặt tiền đường nội khu rộng, bạn c&oacute; thể di chuyển thuận tiện tới trung t&acirc;m TPHCM chưa tới 5km, gắn li&ecirc;n&nbsp;c&aacute;c trục đường lớn&nbsp;giao th&ocirc;ng thuận lợi: Xa Lộ H&agrave; Nội, Đại Lộ Mai Ch&iacute; Thọ với&nbsp;2 trục đường xuy&ecirc;n suốt&nbsp;L&ecirc; Văn Việt v&agrave;&nbsp;Đỗ Xu&acirc;n Hợp kết nối mọi nơi cho cư d&acirc;n thuận tiện di chuyển nơi đ&acirc;u, đặc biệt&nbsp;c&aacute;c quận trung t&acirc;m như&nbsp;Q1,Q2, B&igrave;nh Thạnh, kh&ocirc;ng chỉ vậy vị tr&iacute; c&ograve;n thuận tiện cho cư d&acirc;n muốn di chuyển ra c&aacute;c tỉnh&nbsp;B&igrave;nh Dương, Đồng Nai chỉ trong 15 ph&uacute;t.</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/vi-tri-du-an.png?1470901954550" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 9 VIEW&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<h4><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><br /></a><a href="#dangkyxemnha">&nbsp;</a></h4>', '<div id="wizardtab3" class="tab-pane active">\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Một trong nhưng ưu đi&ecirc;m nội trội l&agrave; C&ocirc;ng vi&ecirc;n rộng gần 6.000m<sup>2</sup>&nbsp;ngay tại</span><strong>&nbsp;<span style="color: #800000; font-size: 12pt;">9View Apartment</span></strong>&nbsp;đ<span style="color: #000000; font-size: 12pt;">ược thiết kế th&agrave;nh 6 khu chức năng kh&aacute;c nhau mang đến sự tiện nghi v&agrave; gi&uacute;p cư d&acirc;n &ldquo;sống khỏe, sống xanh&rdquo;.</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;"><strong>C&ocirc;ng vi&ecirc;n 6 trong 1</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-1.jpg?1470902292215" width="100%" /></a></p>\r\n<p style="text-align: justify;">&nbsp;<a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-2.jpg?1470902411382" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;"><em>1. S&acirc;n thể thao cầu l&ocirc;ng, b&oacute;ng rổ, b&oacute;ng chuyền d&agrave;nh cho c&aacute;c bạn trẻ năng động thoả sức luyện tập c&aacute;c m&ocirc;n thể thao m&igrave;nh y&ecirc;u th&iacute;ch</em></span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-3.jpg?1470902507421" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">2. Khu thể dục dụng cụ vận động đ&aacute;p ứng đầy đủ nhu cầu luyện tập của mọi độ tuổi</span></p>\r\n<p>&nbsp;</p>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 9 VIEW&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-4.jpg?1470902539615" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">3. Khu vui chơi trẻ em gi&uacute;p b&eacute; r&egrave;n luyện thể lực v&agrave; tr&iacute; tuệ</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-5.jpg?1470902575188" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">4. Vườn yoga tr&ecirc;n thảm cỏ xanh tươi gi&uacute;p bạn nhẹ nh&agrave;ng tr&uacute;t bỏ những căng thẳng</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-6.jpg?1470902607683" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">5. Vườn thư gi&atilde;n l&agrave; nơi bạn c&oacute; thể c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave; tản bộ hoặc tham gia c&aacute;c tr&ograve; chơi tập thể</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-7.jpg?1470902641913" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">6. Vườn BBQ ngo&agrave;i trời kết nối bạn b&egrave; với c&aacute;c buổi tiệc nướng vui tươi</span></p>\r\n<h2><strong>Tiện &iacute;ch nội khu</strong></h2>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-1.jpg?1470902917050" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">Khu mua sắm sầm uất</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-2.jpg?1470902957053" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">C&ocirc;ng vi&ecirc;n với 6 khu chức năng đa dạng</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-3.jpg?1470902999598" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">Khu vui chơi trẻ em</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-4.jpg?1470903031795" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">Ph&ograve;ng tập GYM n&acirc;ng cao sức khỏe</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-5.jpg?1470903062673" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">Spa liệu ph&aacute;p thư gi&atilde;n cho tinh thần</span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-6.jpg?1470903092381" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">Nh&agrave; trẻ ti&ecirc;u chuẩn ươm mầm tương lai</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-7.jpg?1470903144636" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">Si&ecirc;u thị mini tiện lợi ngay tầng trệt</span></p>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NHẬN TIẾN ĐỘ THANH TO&Aacute;N 9 VIEW</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p>&nbsp;</p>\r\n<h2><span style="font-size: 12pt; color: #000000;"><strong>Tiện &iacute;ch ngoại khu</strong></span></h2>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-1_2.jpg?1470903349385" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">Hệ thống si&ecirc;u thị đa dạng h&agrave;ng h&oacute;a</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-2_2.jpg?1470903381698" width="100%" /></a><br /><span style="font-size: 12pt; color: #000000;">Trung t&acirc;m thương mại với những thương hiệu h&agrave;ng đầu</span></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-3_2.jpg?1470903409921" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">Trương học</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-4_2.jpg?1470903459602" width="100%" /></a><br /><span style="color: #000000; font-size: 12pt;">Trường học quốc tế danh tiến</span></p>\r\n</div>\r\n<div class="linksShare">&nbsp;\r\n<h4 style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NHẬN TIẾN ĐỘ THANH TO&Aacute;N 9 VIEW</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<h4><a href="../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha"><br /></a><a href="#dangkyxemnha">&nbsp;</a></h4>\r\n</div>', '<p><span style="font-size: 12pt; color: #000000;"><strong>SƠ ĐỒ MẶT BẰNG 9 VIEW</strong></span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Khu&ocirc;n vi&ecirc;n nội khu rộng với tổng diện t&iacute;ch 3ha nhưng mật độ x&acirc;y dựng chỉ 40% ngo&agrave;i ra l&agrave;&nbsp;c&aacute;c khu vui chơi trẻ em, c&ocirc;ng vi&ecirc;n,&nbsp;s&acirc;n tập thể dục v&agrave; được che phủ bởi c&acirc;y xanh tạo n&ecirc;n&nbsp;một kh&ocirc;ng gian sống chất lượng cao,&nbsp;tr&agrave;n ngập m&agrave;u xanh. Căn hộ được chia th&agrave;nh 3 Block (A,B,C) với 17 tầng trong đ&oacute;:&nbsp;1 tầng hầm để xe, 2 tầng thương mại v&agrave;&nbsp;từ tầng 3-&gt;17 l&agrave; tầng căn hộ&nbsp;với tổng 690 căn hộ sở hữu nhiều diện t&iacute;ch kh&aacute;c nhau từ 58-&gt;87 m2 (2PN - 3PN).</span></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-9-view.jpg?1470903554649" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 9 VIEW&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', '<p><strong>C&ocirc;ng ty Cổ phần Đầu tư Kinh doanh&nbsp;Địa ốc&nbsp;Hưng Thịnh</strong></p>\r\n<p>Trụ sở ch&iacute;nh: 110 -112 Trần Quốc Toản, Q3, TP.HCM</p>\r\n<p>Ph&ograve;ng giao dịch: Số 45 Ho&agrave;ng Việt, P.4,&nbsp;Q. T&acirc;n B&igrave;nh,&nbsp;TP.HCM</p>\r\n<p>Li&ecirc;n hệ chủ đầu tư: (08)38.788.789 - 0919.889.333 &nbsp;</p>\r\n<p>Email:tuanhoang@hungthinhland.com</p>', '<div id="wizardtab6" class="tab-pane active">\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>H&Igrave;NH ẢNH CĂN HỘ MẪU 9 VIEW</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nha-mau-9-view-1.jpg?1470903786075" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nha-mau-9-view-2.jpg?1470903817575" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nha-mau-9-view-3.jpg?1470903846898" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nha-mau-9-view-4.jpg?1470903887300" width="100%" /></a></p>\r\n</div>\r\n<div class="linksShare" style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY XEM NH&Agrave; MẪU 9 VIEW&nbsp;</strong></span>&nbsp;\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<h4 style="text-align: center;"><span style="color: #ff0000;"><strong>&nbsp;</strong></span></h4>\r\n</div>', 6, '', '', '', 'logo_9view.jpg', '#faf7f7', '9 View', '#ff7800');
INSERT INTO `duan` (`duan_id`, `title`, `description`, `detail`, `is_featured`, `post_time`, `image`, `is_dautu`, `is_giatot`, `is_duanmoi`, `price`, `thongtin`, `vitri`, `tienich`, `matbang`, `tintuc`, `hinhanh`, `vitrihienthi`, `image_alt`, `title_tag`, `meta_description`, `logo`, `text_color`, `footer_color`, `header_color`) VALUES
(3, 'LAVITA GARDEN THỦ ĐỨC - PHONG CÁCH SINGAPOR', '<p style="text-align: justify;"><strong><span style="color: #800000;">LAVITA GARDEN</span></strong> mang cảm nhận ri&ecirc;ng của kh&ocirc;ng gian xanh.Được đầu tư về tri thức v&agrave; nguồn cảm hứng từ căn hộ Singapor tạo n&ecirc;n sự kh&aacute;c biệt cho cuộc sống hiện đại.B&ecirc;n cạnh đ&oacute; l&agrave; t&acirc;m nh&igrave;n v&agrave; kh&ocirc;ng gian tr&ecirc;n cao của căn hộ.Một khung canh t&agrave;u cao tốc chạy len l&otilde;i trong th&agrave;nh phố hiện ra trước ban c&ocirc;ng mỗi căn hộ.</p>', '<p><strong><a href="http://hungthinhland.biz/can-ho-lavita-garden-vuon-trong-pho.html">Căn hộ Lavita Garden</a></strong>&nbsp;nằm ngay Ng&atilde; tư B&igrave;nh Th&aacute;i,c&aacute;ch ga Metro B&igrave;nh Th&aacute;i 100m.Đ&acirc;y l&agrave; được cho l&agrave; dự &aacute;n tổng kết lại&nbsp;năm 2015 của Hưng Thịnh. Vậy,dự &aacute;n căn hộ Lavita Garden n&agrave;y c&oacute; g&igrave; đặc biệt,ưu nhược điểm,c&oacute; n&ecirc;n mua đầu tư hay mua để ở kh&ocirc;ng? B&agrave;i đ&aacute;nh gi&aacute; sau đ&acirc;y của ch&uacute;ng t&ocirc;i hi vọng gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; được c&aacute;i nh&igrave;n to&agrave;n diện nhất về dự &aacute;n.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Phoi%20Canh%20Lavita%20garden.jpg" alt="" /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>Video Clip Lavita garden :</strong></p>\r\n<p><strong>&nbsp;Mời Qu&yacute; kh&aacute;ch xem trước Video clip Lavita để c&oacute; c&aacute;i nh&igrave;n bao qu&aacute;t về dự &aacute;n &nbsp;</strong></p>\r\n<p><iframe src="https://www.youtube.com/embed/21EDLWokpAY" width="350" height="200" frameborder="0" scrolling="no" allowfullscreen="allowfullscreen" data-mce-fragment="1"></iframe></p>\r\n<h3><span id="V_tr_cn_hLavita_Garden_c_g_khc_bit">Vị tr&iacute; căn hộ&nbsp;<a href="http://hungthinhland.biz/can-ho-lavita-garden-vuon-trong-pho.html">Lavita Garden</a> c&oacute; g&igrave; kh&aacute;c biệt?</span></h3>\r\n<p>Thời gian gần đ&acirc;y,thị trường bất động sản khu Đ&ocirc;ng trỗi dậy mạnh mẽ nhờ yếu tố hạ tầng.Từ năm 2012-2020,c&oacute; tới 11 dự &aacute;n hạ tầng lớn nhỏ với tổng mức đầu tư l&ecirc;n tới 250.000 tỷ đồng,đ&aacute;ng ch&uacute; &yacute; nhất l&agrave; c&aacute;c tuyến v&agrave;nh đai 2,3,4 v&agrave; tuyến Metro Bến Th&agrave;nh-Suối Ti&ecirc;n.</p>\r\n<h4><span id="Cn_h_Lavita_Garden_Gn_Ga_Metro">Căn hộ Lavita Garden Liền Kề &nbsp;Ga Số 10 Của Tuyến Metro Số 1 :</span></h4>\r\n<p>Đặc biệt l&agrave; tuyến Metro,được đ&aacute;nh gi&aacute; l&agrave; yếu tố quan trọng sẽ l&agrave;m thay đổi thị trường bất động sản khu Đ&ocirc;ng.Một loạt c&aacute;c dự &aacute;n căn hộ chung cư được mở b&aacute;n gần đ&acirc;y tại khu Đ&ocirc;ng đều nhấn mạnh th&ocirc;ng điệp<em>&nbsp;&ldquo;căn hộ gần tuyến&nbsp;Metro&rdquo;</em>. Tuy nhi&ecirc;n theo ch&uacute;ng t&ocirc;i,<strong>gần tuyến Metro&nbsp;</strong>v&agrave;&nbsp;<strong>gần Ga Metro</strong>&nbsp;l&agrave; 2 kh&aacute;i niệm&nbsp;kh&ocirc;ng giống nhau<strong>:</strong>&nbsp;v&igrave; t&agrave;u điện Metro chỉ đ&oacute;n trả kh&aacute;ch tại c&aacute;c Ga(tuyến Metro Bến Th&agrave;nh-Suối Ti&ecirc;n gồm 14 Ga, mỗi Ga c&aacute;ch nhau khoảng 2km).Do vậy,đi t&agrave;u điện Metro thực sự thuận tiện nhất sẽ l&agrave;&nbsp;<strong>gần Ga Metro</strong>. V&agrave; đ&acirc;y l&agrave; điểm m&agrave; ch&uacute;ng t&ocirc;i đ&aacute;nh gi&aacute; cao&nbsp;<strong>vị tr&iacute; căn hộ Lavita Garden</strong>. Từ căn hộ Lavita đi ra Ga Metro B&igrave;nh Th&aacute;i(Ga số 10) chỉ 100m.</p>\r\n<p><img class="aligncenter size-full wp-image-1710 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-vi-tri1.jpg" alt="can ho lavita garden vi tri" /></p>\r\n<h4><span id="Cn_h_Lavita_Garden_GnNg_T_Bnh_Thi"><strong>Căn hộ Lavita Garden</strong> Gần&nbsp;Ng&atilde; Tư B&igrave;nh Th&aacute;i</span></h4>\r\n<p><strong>Căn hộ Lavita Garden</strong>&nbsp;của Hưng Thịnh chỉ c&aacute;ch Ng&atilde; tư B&igrave;nh Th&aacute;i 100m. Đ&acirc;y l&agrave; n&uacute;t giao th&ocirc;ng quan trọng,l&agrave; nơi liền kề những tuyến đường lớn như Xa lộ H&agrave; Nội, Đỗ Xu&acirc;n Hợp, đường V&agrave;nh Đai 2, đường Phạm Văn Đồng&hellip;</p>\r\n<p>Trong khi chờ tuyến Metro Bến Th&agrave;nh-Suối Ti&ecirc;n đi v&agrave;o hoạt động, th&igrave; :</p>\r\n<ul>\r\n<li>Từ căn hộ Lavita Garden dễ d&agrave;ng di chuyển về&nbsp;<strong>trung t&acirc;m&nbsp;quận 2,B&igrave;nh Thạnh,quận 1</strong>&nbsp;qua tuyến Xa Lộ H&agrave; Nội-Điện Bi&ecirc;n Phủ,hoặc tuyến Xa Lộ H&agrave; Nội-Đại lộ Mai Ch&iacute; Thọ.</li>\r\n<li>Từ căn hộ Lavita đi&nbsp;<strong>quận 9, Thủ Đức</strong>&nbsp;qua trục đường xương sống l&agrave; Xa Lộ H&agrave; Nội rất tiện lợi.</li>\r\n<li>C&ograve;n đi về hướng&nbsp;<strong>G&ograve; Vấp,T&acirc;n B&igrave;nh</strong>&nbsp;c&oacute; tiện kh&ocirc;ng? Từ căn hộ Lavita Garden đi theo đường số 2 sẽ ra tới Phạm Văn Đồng. Sau khi đường V&agrave;nh đai 2 ho&agrave;n th&agrave;nh th&igrave; cư d&acirc;n tại đ&acirc;y sẽ đi theo đường v&agrave;nh đai 2 ra Phạm Văn Đồng c&ograve;n r&uacute;t ngắn thời gian hơn nữa .</li>\r\n</ul>\r\n<h3><span id="Quy_m_v_tin_ch_cn_h_Lavita">Quy m&ocirc; v&agrave; tiện &iacute;ch căn hộ Lavita</span></h3>\r\n<p>Với vị tr&iacute; gần ngay Nh&agrave; Ga Metro như tr&ecirc;n th&igrave; những c&aacute;i t&ecirc;n kiểu như&nbsp;<em>căn hộ Metro B&igrave;nh Th&aacute;i</em>&nbsp;hay&nbsp;<em>căn hộ Metro Apartment</em>&nbsp;l&agrave; lựa chọn kh&ocirc;ng tồi&nbsp;.Vậy tại sao căn hộ Lavita Garden lại được đặt t&ecirc;n l&agrave; &hellip;Lavita Garden?</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/phoi_canh_lavita.jpg" alt="" /></p>\r\n<p>Theo t&ocirc;i,Chủ đầu tư Hưng Thịnh muốn x&acirc;y dựng n&ecirc;n một&nbsp;<strong>căn hộ Lavita Garden</strong>&nbsp;với m&ocirc;i trường sống tốt,theo ti&ecirc;u chuẩn Xanh, đ&uacute;ng nghĩa Garden. Đ&acirc;y l&agrave; chia sẻ của bộ phận tư vấn thiết kế dự &aacute;n Lavita Garden:<em>&nbsp;&ldquo;Khu vườn Lavita sẽ l&agrave; điểm nhấn cho dự &aacute;n.Đ&acirc;y l&agrave; nơi c&aacute;c cư d&acirc;n dạo bộ thư th&aacute;i dưới những v&ograve;m hoa. &Acirc;m thanh r&oacute;c r&aacute;ch của từng d&ograve;ng nước len qua khe đ&aacute;, h&ograve;a quyện giữa kh&ocirc;ng gian xanh ng&aacute;t của khu vườn sẽ x&oacute;a tan mọi căng thẳng, bộn bề của cuộc sống thường nhật.&rdquo;</em></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Vuon%20choi%20tre%20em%20tai%20Lavita%20Garden.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><strong>C&ocirc;ng vi&ecirc;n ph&aacute;t triển tr&iacute; tuệ v&agrave; kỹ năng cho thiếu nhi độc đ&aacute;o tại Lavita garden</strong></p>\r\n<p>&nbsp;</p>\r\n<ul>\r\n<li>Quỹ đất c&oacute; diện t&iacute;ch 9.924 m2</li>\r\n<li>Mật độ x&acirc;y dựng l&agrave; 35%</li>\r\n<li>Tổng thể dự &aacute;n c&oacute; 2 block cao 19 tầng với 648 căn hộ</li>\r\n<li>Diện t&iacute;ch d&agrave;nh cho giao th&ocirc;ng,c&ocirc;ng vi&ecirc;n,hồ bơi&hellip;l&ecirc;n tới 6.417 m2</li>\r\n</ul>\r\n<p>Diện t&iacute;ch 6.417 m2 d&agrave;nh ri&ecirc;ng cho giao th&ocirc;ng,c&ocirc;ng vi&ecirc;n c&acirc;y xanh,hồ bơi&hellip; l&agrave; một diện t&iacute;ch kh&aacute; lớn. Ngo&agrave;i ta, tại tầng 1 của căn hộ Lavita Garden c&ograve;n quy hoạch c&aacute;c tiện &iacute;ch như Trung t&acirc;m thương mại, Shophouse, Nh&agrave; trẻ,Cafe,Ph&ograve;ng Gym&hellip;đ&aacute;p ứng nhu cầu cho cư d&acirc;n tại đ&acirc;y.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/ho%20boi%20tai%20Lavita%20garden.PNG" alt="http://www.nhadatmuaban.com.vn/" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Phong%20Gym%20tai%20Lavita.PNG" alt="http://www.nhadatmuaban.com.vn/" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/khu%20Spa%20tai%20Lavita.PNG" alt="http://www.nhadatmuaban.com.vn/" /></p>\r\n<p>Ngo&agrave;i c&aacute;c tiện &iacute;ch b&ecirc;n trong&nbsp;dự &aacute;n, cư d&acirc;n căn hộ Lavita Garden c&ograve;n dễ d&agrave;ng tiếp cận c&aacute;c tiện &iacute;ch xung quanh như:</p>\r\n<ul>\r\n<li>Ng&atilde; tư B&igrave;nh Th&aacute;i (Ga số 10 thuộc tuyến metro Bến Th&agrave;nh &ndash; Suối Ti&ecirc;n): 1 ph&uacute;t.</li>\r\n<li>Co.opmart Thủ Đức: 5 ph&uacute;t.</li>\r\n<li>Bệnh viện Qu&acirc;n d&acirc;n Y Miền Đ&ocirc;ng: 6 ph&uacute;t.</li>\r\n<li>Vincom Thủ Đức: 8 ph&uacute;t.</li>\r\n<li>C&ocirc;ng vi&ecirc;n giải tr&iacute; Suối Ti&ecirc;n: 9 ph&uacute;t.</li>\r\n<li>L&agrave;ng Đại học Thủ Đức: 10 ph&uacute;t.</li>\r\n<li>Si&ecirc;u thị metro An Ph&uacute;: 10 ph&uacute;t.</li>\r\n<li>Parkson Cantavil: 10 ph&uacute;t.</li>\r\n<li>Trường British International: 13 ph&uacute;t.</li>\r\n</ul>\r\n<h3><span id="Cc_loi_din_tch_cn_h_Lavita_Garden">C&aacute;c loại diện t&iacute;ch căn hộ Lavita Garden</span></h3>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/mat_bang%20Lavita%20garden.jpg" alt="mat bang lavita garden" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<figure id="attachment_1738" class="wp-caption aligncenter"><img class="size-full wp-image-1738 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-mat-bang-block-A.jpg" alt="Mặt bằng block A căn hộ Lavita Garden" />\r\n<p>&nbsp;</p>\r\n<figcaption class="wp-caption-text"><em><strong>Mặt bằng block A căn hộ Lavita Garden</strong></em></figcaption>\r\n</figure>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<figure id="attachment_1739" class="wp-caption aligncenter"><img class="size-full wp-image-1739 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-mat-bang-block-B.jpg" alt="Mặt bằng block B căn hộ Lavita Garden" /><br />\r\n<figcaption class="wp-caption-text"><strong>Mặt bằng block B căn hộ Lavita Garden</strong></figcaption>\r\n</figure>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Ai n&ecirc;n mua căn hộ Lavita Garden?</strong>&nbsp;Căn hộ Lavita Garden với c&aacute;c loại diện t&iacute;ch từ 51 m2 -&gt; 71 m2, c&oacute; 1 Ph&ograve;ng ngủ v&agrave; 2 Ph&ograve;ng ngủ. Với thiết kế như vậy th&igrave; căn hộ n&agrave;y ph&ugrave; hợp nhất cho người độc th&acirc;n&nbsp;hoặc c&aacute;c gia đ&igrave;nh&nbsp;trẻ c&oacute; 2-4 th&agrave;nh vi&ecirc;n.</p>\r\n<p>Nh&igrave;n v&agrave;o mặt bằng tổng thể,căn hộ Lavita Garden chia l&agrave;m 2 block A,B: ở giữa 2 block c&oacute; hồ bơi,c&ocirc;ng vi&ecirc;n,rất th&ocirc;ng tho&aacute;ng. Ta cũng sẽ dễ d&agrave;ng nhận ra,giữa 2 căn hộ Lavita sẽ c&oacute; khoảng trống ,<strong>nhằm tăng hệ số g&oacute;c cho c&aacute;c&nbsp;căn hộ Lavita.</strong>Đ&acirc;y l&agrave; điểm nhấn m&agrave; t&ocirc;i thường thấy trong thiết kế c&aacute;c căn hộ của Chủ đầu tư Hưng Thịnh thời gian gần đ&acirc;y.</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/tien_ich.jpg" alt="ho boi tai Lavita garden" /></p>\r\n<p><strong><img src="http://sanhungthinh.com.vn/upload/images/sieu%20thi%20tai%20Lavita.PNG" alt="http://www.nhadatmuaban.com.vn/" /></strong></p>\r\n<p>&nbsp;</p>\r\n<h4><strong><span id="Cn_h_Lavita_Garden_c_1_Phng_ng">Lavita Garden c&oacute; căn hộ 1 Ph&ograve;ng ngủ</span></strong></h4>\r\n<p><img class="aligncenter size-full wp-image-1740 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-1-phong-ngu.png" alt="can ho lavita 1 phong ngu" width="411" height="332" /></p>\r\n<p>Căn hộ Lavita Garden c&oacute; 1 Ph&ograve;ng ngủ diện t&iacute;ch 51.10 m2. Căn hộ n&agrave;y ph&ugrave; hợp nhất cho người độc th&acirc;n hoặc gia đ&igrave;nh vợ chồng trẻ mới cưới.</p>\r\n<h4><span id="Cn_h_Lavita_Garden_c_2_Phng_ng">Căn hộ Lavita Garden c&oacute; 2 Ph&ograve;ng ngủ</span></h4>\r\n<p><img class="aligncenter size-full wp-image-1741 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-2-phong-ngu-loai-1.png" alt="can ho lavita garden 2 phong ngu loai 1" width="480" height="307" /></p>\r\n<p><img class="aligncenter size-full wp-image-1742 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-2-phong-ngu-loai-2.png" alt="can ho lavita garden 2 phong ngu loai 2" width="526" height="245" /><img class="aligncenter size-full wp-image-1743 tie-appear" src="http://canhoharmona.info/wp-content/uploads/2015/10/can-ho-lavita-garden-2-phong-ngu-loai-3.png" alt="can ho lavita garden 2 phong ngu loai 3" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20a1_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20a2_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20A7_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20A8_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20a10_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20A13%2CB7_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20B12_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Can%20ho%20B15_Lavita.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><strong>C&aacute;c căn hộ Lavita Garden 2 Ph&ograve;ng ngủ&nbsp;c&oacute; điểm chung l&agrave;:</strong>&nbsp;c&aacute;c kh&ocirc;ng gian được bố tr&iacute; t&aacute;ch biệt,như 2 Ph&ograve;ng ngủ được đẩy về 1 b&ecirc;n,kh&ocirc;ng gian Ph&ograve;ng kh&aacute;ch+B&agrave;n ăn+Bếp đẩy về 1 b&ecirc;n. Thiết kế như vậy sẽ c&oacute; ưu điểm l&agrave; tận dụng được tối đa diện t&iacute;ch sử dụng căn hộ.</p>\r\n<p><strong>Một số kh&aacute;ch h&agrave;ng nhờ t&ocirc;i ph&acirc;n t&iacute;ch n&ecirc;n mua căn hộ Lavita Garden 2 Ph&ograve;ng ngủ loại n&agrave;o?</strong>&nbsp;N&ecirc;n mua Căn hộ diện t&iacute;ch 59.58 m2, 64.78 m2, 68.76 m2, 69.78 m2, 71.40 m2, 71.68 m2? Trong số c&aacute;c căn n&agrave;y th&igrave; c&oacute; căn 71.40 m2 v&agrave; 71.68 m2 l&agrave; căn g&oacute;c, sẽ c&oacute; 2 mặt view th&ocirc;ng tho&aacute;ng, tuy nhi&ecirc;n gi&aacute; sẽ cao hơn c&aacute;c căn c&ograve;n lại. C&ograve;n những căn c&ograve;n lại Qu&yacute; kh&aacute;ch n&ecirc;n ch&uacute; &yacute; th&ecirc;m về thiết kế như sau:</p>\r\n<ul>\r\n<li><strong>Căn hộ Lavita Garden diện t&iacute;ch 59.58 m2 v&agrave; 64.78 m2</strong>: c&aacute;c căn n&agrave;y sẽ lấy gi&oacute; từ ban c&ocirc;ng ph&ograve;ng kh&aacute;ch+ cửa sổ ph&ograve;ng ngủ lớn. C&ograve;n ph&ograve;ng ngủ nhỏ sẽ kh&ocirc;ng c&oacute; cửa sổ. Tuy nhi&ecirc;n nhờ lợi thế diện t&iacute;ch nhỏ n&ecirc;n tổng gi&aacute; b&aacute;n sẽ thấp hơn. Th&ecirc;m một lưu &yacute; nữa về căn hộ Lavita diện t&iacute;ch n&agrave;y l&agrave; tuy c&oacute; 2 Ph&ograve;ng ngủ nhưng chỉ c&oacute; 1 WC.</li>\r\n<li><strong>Căn hộ Lavita Garden diện t&iacute;ch&nbsp;68.76 m2 v&agrave; 69.78 m2</strong>: c&aacute;c căn n&agrave;y th&ocirc;ng tho&aacute;ng hơn nhờ lấy gi&oacute; từ ban c&ocirc;ng Ph&ograve;ng kh&aacute;ch+cửa sổ Ph&ograve;ng ngủ lớn+ cửa l&ocirc; gia(khoảng kh&ocirc;ng n&agrave;y c&oacute; thể th&ocirc;ng tới bếp+ph&ograve;ng ngủ nhỏ). Lợi thế l&agrave; th&ocirc;ng tho&aacute;ng hơn nhưng gi&aacute; tiền sẽ cao hơn v&igrave; c&oacute; diện t&iacute;ch lớn hơn. Căn hộ Lavita loại n&agrave;y c&oacute; 2 Ph&ograve;ng ngủ v&agrave; 2WC.</li>\r\n</ul>\r\n<p><strong>H&igrave;nh ảnh căn hộ Mẫu Lavita Garden&nbsp;</strong></p>\r\n<p><em>Loại căn 71m2 &ndash; 2 ph&ograve;ng ngủ &amp; 2 wc</em></p>\r\n<p><em><img src="http://sanhungthinh.com.vn/upload/images/can%20ho%2071m2.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></em></p>\r\n<p>&nbsp;</p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/phong%20bep%20can%20ho%20mau_lavita%20garden(1).jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/phong%20khach%202%20can%20ho%20mau_lavita%20garden.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Phong%20khach%20can%20ho%20mau_Lavita%20garden.jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>\r\n<p><img src="http://sanhungthinh.com.vn/upload/images/Phong%20khach%20can%20ho%20mau_Lavita%20garden(1).jpg" alt="http://www.nhadatmuaban.com.vn/Du_An_Can_Ho_Lavita_Garden__47" /></p>', 1, '2016-11-11 07:26:22', 'lavita-hinh-3_1.jpg', 1, 1, 1, '1.3 tỷ', '<p><span style="color: #333333;"><strong>&nbsp;<span style="font-size: 12pt; color: #000000;">CĂN HỘ LAVITA GARDEN</span></strong></span></p>\r\n<p>&nbsp;<a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/phoi-canh-lavita.jpg?1470904443366" width="100%" /></a></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;"><span style="color: #800000;"><strong>Căn hộ</strong>&nbsp;<strong><a style="color: #800000;" href="../duan-lavita-garden-khu-vuon-diem-to-cuoc-song-tron-day-3.html#thongtin">LAVITA GARDEN</a></strong></span>&nbsp;<span style="color: #000000;">Thủ Đức, Sở hữu vị tr&iacute; trắc địa,chỉ v&agrave;i bước ch&acirc;n l&agrave; bạn c&oacute; thể di chuyển tới tuyến cao tốc Metro, Xa lộ H&agrave; Nội, phong c&aacute;ch Singapor được lấy cảm hứng cho thiết kế căn hộ, sang trọng mang lại cho bạn một cuộc sống thoải m&aacute;i&nbsp;đầy đủ tiện nghị... Căn hộ được x&acirc;y dựng thiết kế tr&ecirc;n &yacute; tưởng như một khu vườn đ&iacute;ch thực m&agrave; những ai từng đến nơi đ&acirc;y sẽ kh&ocirc;ng khỏi phần bất ngờ với mật độ x&acirc;y dựng thấp chỉ khoảng 35,3%, đ&acirc;y thật sự l&agrave; điều tuyệt vời cho những ai y&ecirc;u&nbsp;th&iacute;ch những khoảng xanh, th&iacute;ch bầu trời v&agrave; một nơi để tản bộ y&ecirc;n b&igrave;nh v&agrave;o mỗi buổi s&aacute;ng hay chiều về</span>.</span>&nbsp;<span style="font-size: 12pt;"><strong><span style="color: #800000;">Căn hộ Lavita Garden</span></strong></span>&nbsp;<span style="color: #000000; font-size: 12pt;">sở hữu một địa thế thuận lợi cho việc di chuyển đến bất kỳ nơi đ&acirc;u tại c&aacute;c quận trung t&acirc;m: Quận 1, Quận 2,.. cũng như c&aacute;c quận ngoại th&agrave;nh: G&ograve; Vấp, Thủ Đức, Quận 9.</span></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<h4><span style="font-size: 12pt; color: #000000;"><strong>VỊ TR&Iacute;&nbsp;LAVITA GARDEN</strong></span></h4>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Vi tr&iacute; đắc địa nằm ngay trục giao th&ocirc;ng ch&iacute;nh nối trung t&acirc;m S&agrave;i G&ograve;n ra Thủ Đức, ở</span>&nbsp;<span style="color: #800000; font-size: 12pt;"><a style="color: #800000;" href="../duan-lavita-garden-khu-vuon-diem-to-cuoc-song-tron-day-3.html#thongtin"><strong>Căn hộ Lavita Garden</strong></a></span><span style="font-size: 12pt;">&nbsp;<span style="color: #000000;">sở hữu một địa thế qu&aacute; ho&agrave;n hảo cho việc di chuyển đến bất kỳ nơi đ&acirc;u tại c&aacute;c quận trung t&acirc;m: Quận 1, Quận 2,.. cũng như c&aacute;c quận ngoại th&agrave;nh: G&ograve; Vấp, Thủ Đức, Quận 9. Đặc biệt</span></span>&nbsp;<span style="font-size: 12pt;"><strong><span style="color: #800000;">Lavita Garden</span>&nbsp;</strong></span><span style="color: #000000; font-size: 12pt;">nằm tr&ecirc;n&nbsp;đường V&agrave;nh đai 2 lộ giới&nbsp;kết nối&nbsp;đoạn từ Xa lộ H&agrave; Nội&nbsp;đến đường Phạm Văn Đồng.</span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/vi-tri-lavita.gif?1470904483991" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<div><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-lavita.jpg?1470904534484" width="100%" /></a></div>\r\n<div>&nbsp;</div>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>TIỆN &Iacute;CH VƯỢT TRỘI</strong></span></p>\r\n<div>\r\n<ul>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Căn hộ Lavita Garden&nbsp;được trang bị hệ thống thang m&aacute;y lớn,hiện đại, 1 thang m&aacute;y d&agrave;nh ri&ecirc;ng cho trường hợp khẩn cấp,đặc biệt, 2 thang bộ v&agrave; một cầu thang ngo&agrave;i.</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">C&oacute; nh&agrave;&nbsp;Nh&agrave; trẻ, khu mua sắm, cafe, spa,&nbsp;ph&ograve;ng tập thể dục hiện đại.</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Tuyệt vời&nbsp;hơn khi&nbsp;&nbsp;Lavita Garden&nbsp;được x&acirc;y dựng theo một ph&ograve;ng c&aacute;ch mở&nbsp;c&oacute; c&ocirc;ng vi&ecirc;n c&acirc;y xanh, hồ nước. Khu vui chơi giải tr&iacute; cho trẻ em... rộng r&atilde;i, sạch đẹp v&agrave; tho&aacute;ng m&aacute;t.</span></li>\r\n</ul>\r\n</div>\r\n<div><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-lavita.jpg?1470904578619" width="100%" /></a></div>\r\n<div>&nbsp;</div>\r\n<ul>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Camera c&oacute; trong hầm xe, thang m&aacute;y, v&agrave; h&agrave;nh lang từng tầng</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Hệ thống cấp nước, tho&aacute;t nước, cấp điện: hệ thống của th&agrave;nh phố.</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Hệ thống b&aacute;o ch&aacute;y, b&aacute;o kh&oacute;i: tự động trong từng căn hộ, v&ograve;i phun nước v&agrave; b&igrave;nh chữa ch&aacute;y được trang bị ngay h&agrave;nh lang mỗi tầng</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Hệ thống an ninh được bảo vệ 24/24 bởi Cty quản l&yacute; t&ograve;a nh&agrave; chuy&ecirc;n nghiệp.</span></li>\r\n</ul>\r\n<div>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<div><span style="font-size: 12pt; color: #000000;"><strong>Tổng quan dự &aacute;n:</strong></span></div>\r\n</div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">Tổng diện t&iacute;ch khu đất: 9924,4 m2</span><br /><span style="font-size: 12pt; color: #000000;">Thiết kết chiều cao:19 tầng </span><br /><span style="font-size: 12pt; color: #000000;">Chi tiết tầng: 1 tầng hầm + 1 tầng thương mại + &nbsp;01 tầng s&acirc;n thượng</span><br /><span style="font-size: 12pt; color: #000000;">Block: 2 Block</span><br /><span style="font-size: 12pt; color: #000000;">Tổng số căn hộ: 648 căn</span><br /><span style="font-size: 12pt; color: #000000;">Diện t&iacute;ch:&nbsp;51-&gt; 71m2 (1-2&nbsp;ph&ograve;ng ngủ)</span></p>', '<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Tại</span>&nbsp;<span style="color: #800000; font-size: 12pt;"><strong>Lavita Garden</strong></span>,<span style="color: #000000; font-size: 12pt;"> một quang canh xanh tươi hiện ra mỗi s&aacute;ng sớm,chỉ v&agrave;i bước ch&acirc;n l&agrave; cư d&acirc;n đ&atilde; đến ngay ga số 10 - B&igrave;nh Th&aacute;i của tuyến metro Bến Th&agrave;nh - Suối Ti&ecirc;n. Khi tuyến metro n&agrave;y được đưa v&agrave;o sử dụng sẽ kết nối Q.Thủ Đức, Q.2, Q.9 với trung t&acirc;m th&agrave;nh phố v&agrave; c&aacute;c quận l&acirc;n cận trong v&agrave;i ph&uacute;t di chuyển. Tuyến metro được so s&aacute;nh như mạch m&aacute;u giao th&ocirc;ng của TP.HCM sẽ gi&uacute;p cư d&acirc;n</span><strong>&nbsp;<span style="color: #800000; font-size: 12pt;">Lavita Garden</span></strong>&nbsp;<span style="color: #000000; font-size: 12pt;">dễ d&agrave;ng tiếp cận c&aacute;c tiện &iacute;ch hiện đại như: si&ecirc;u thị Metro An Ph&uacute;, Big C Cantavil, Parkson Cantavil, trường quốc tế BIS,... v&agrave; nhanh ch&oacute;ng h&ograve;a m&igrave;nh v&agrave;o nhịp sống năng động của th&agrave;nh phố.</span></p>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/vi-tri-lavita_1.gif?1470904736170" width="100%" /></a></p>\r\n<p>&nbsp;</p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', '<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Bạn h&atilde;y cởi mở cuộc sống m&igrave;nh,một cuộc sống hiện đại phong c&aacute;ch trong những căn hộ sang trọng.Sống trong một căn hộ cao tầng kh&ocirc;ng c&oacute; nghĩa l&agrave; bạn kh&ocirc;ng thể thưởng thức được vẻ đẹp v&agrave; sự tươi m&aacute;t của thi&ecirc;n nhi&ecirc;n. Một cuộc sống văn minh với hồ bơi, khu vui chơi trẻ em, si&ecirc;u thị, gym, spa,&hellip;được bao bọc bởi m&ocirc;i trường trong l&agrave;nh v&agrave; sắc m&agrave;u sống động của cỏ c&acirc;y hoa l&aacute; sẽ đ&aacute;p ứng ti&ecirc;u chuẩn sống h&agrave;ng đầu cho c&aacute;c cư d&acirc;n.</span></p>\r\n<div>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>TIỆN &Iacute;CH NỘI KHU</strong></span></p>\r\n</div>\r\n<div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-1_1.jpg?1470905162908" width="100%" /></a></p>\r\n</div>\r\n<div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">Hồ bơi tr&agrave;n bờ hiện đại ngay tầng 2 của dự &aacute;n với tầm nh&igrave;n tho&aacute;ng đ&atilde;ng, trải d&agrave;i theo tuyến metro sẽ mang đến cho bạn kh&ocirc;ng gian sống nghỉ dưỡng đẳng cấp ngay tại khu căn hộ</span></p>\r\n</div>\r\n</div>\r\n<div>\r\n<div>\r\n<p style="text-align: justify;">&nbsp;</p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-2_1.jpg?1470905204663" width="100%" /></a></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">Khu vui chơi trẻ em chuẩn Singapore</span></p>\r\n</div>\r\n<div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">Với c&aacute;c thiết bị nhập từ Singapore, khu vui chơi rộng hơn 1.000m2 sẽ gi&uacute;p trẻ thỏa sức kh&aacute;m ph&aacute; thế giới xung quanh m&igrave;nh, r&egrave;n luyện thể lực v&agrave; ph&aacute;t triển tr&iacute; tuệ.</span></p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>\r\n</div>\r\n<div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">&nbsp;</span></p>\r\n<p style="text-align: justify;"><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-3_1.jpg?1470905252862" width="100%" /></a></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;">Ph&ograve;ng tập gym n&acirc;ng cao sức khỏe&nbsp;</span></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-4_1.jpg?1470905313936" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Spa liệu ph&aacute;p thư gi&atilde;n cho tinh thần&nbsp;&nbsp;</span></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-5_1.jpg?1470905362644" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Khu mua sắm sầm uất&nbsp;</span></p>\r\n</div>\r\n<p><span style="font-size: 12pt; color: #000000;">&nbsp;</span></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-noi-khu-6_1.jpg?1470905454005" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Nh&agrave; trẻ ti&ecirc;u chuẩn ươm mầm tương lai&nbsp;</span></p>\r\n<div>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>TIỆN &Iacute;CH NGOẠI KHU</strong></span></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-1_3.jpg?1470905702543" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Hệ thống si&ecirc;u thị đa dạng h&agrave;ng h&oacute;a&nbsp;</span></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-2_3.jpg?1470905747109" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Trường học quốc tế danh tiếng</span></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/tien-ich-ngoai-khu-3_3.jpg?1470905783549" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Trung t&acirc;m thương mại với những thương hiệu h&agrave;ng đầu</span></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>', '<p style="text-align: justify;"><span style="color: #000000;"><span style="font-size: 12pt;">Một khu vườn tươi s&aacute;ng sẽ l&agrave;m bừng l&ecirc;n sức sống cho những khối kiến tr&uacute;c hi&ecirc;n đại.</span> <span style="font-size: 12pt;">Tại</span></span>&nbsp;<span style="color: #800000; font-size: 12pt;"><strong>Lavita Garden</strong></span>, <span style="color: #000000; font-size: 12pt;">mỗi căn hộ đều được thiết kế hiện đại nhằm tận dụng tối đa diện t&iacute;ch sử dụng cho mọi hoạt động của c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh. Ngo&agrave;i ra, mỗi căn hộ đều c&oacute; những khoảng kh&ocirc;ng tho&aacute;ng đ&atilde;ng gi&uacute;p bạn tạo n&ecirc;n những mảng xanh tươi m&aacute;t cho ng&ocirc;i nh&agrave; của m&igrave;nh.</span></p>\r\n<p style="text-align: justify;"><strong><span style="color: #000000; font-size: 12pt;">MẶT BẰNG TẦNG ĐIỂN H&Igrave;NH TẦNG 2-19</span></strong></p>\r\n<div id="wizardtab4" class="tab-pane active">\r\n<div>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-1.jpg?1470906227799" width="100%" /></a></p>\r\n<div class="title_ge1 mt_20m">\r\n<h2>&nbsp;<span style="font-size: 12pt; color: #000000;">MẪU CĂN HỘ</span></h2>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-2.jpg?1470906260094" width="100%" /></a><br /><br /><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-3.jpg?1470906292435" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;</h4>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-4.jpg?1470906333893" width="100%" /></a><br /><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-5.jpg?1470906376597" width="100%" /></a><br /><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-6.jpg?1470906415628" width="100%" /></a><br /><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-7.jpg?1470906469906" width="100%" /></a><br /><br /><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/mat-bang-8.jpg?1470906499729" width="100%" /></a></p>\r\n</div>\r\n</div>\r\n<div class="linksShare" style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;">&nbsp;</span></div>\r\n<div class="linksShare" style="text-align: center;">\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>', '', '<p>&nbsp;<span style="font-size: 12pt; color: #000000;"><strong>H&Igrave;NH ẢNH NH&Agrave; MẪU CĂN HỘ</strong></span></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nhau-mau-lavita-1.jpg?1470906765177" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nhau-mau-lavita-2.jpg?1470906805241" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nhau-mau-lavita-3.jpg?1470906845162" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/nhau-mau-lavita-4.jpg?1470906905276" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;</h4>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>FLY CAM CĂN HỘ LAVITA GARDEN</strong></span></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/View%20lavita%201.png?1470906943661" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/View%20lavita%202.png?1470906986732" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/View%20lavita%203.png?1470907022917" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/View%20lavita%204.png?1470907091550" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-9view-hung-thinh-quan-9-2.html"><img src="../media/images/View%20lavita%205.png?1470907131531" width="100%" /></a></p>\r\n<p style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY&nbsp;NHẬN BẢNG GI&Aacute; LAVITA GARDEN</strong></span></p>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>', 4, '', '', '', 'logo-lavita.jpg', '#ffffff', 'Lavita Garden', '#008000');
INSERT INTO `duan` (`duan_id`, `title`, `description`, `detail`, `is_featured`, `post_time`, `image`, `is_dautu`, `is_giatot`, `is_duanmoi`, `price`, `thongtin`, `vitri`, `tienich`, `matbang`, `tintuc`, `hinhanh`, `vitrihienthi`, `image_alt`, `title_tag`, `meta_description`, `logo`, `text_color`, `footer_color`, `header_color`) VALUES
(4, 'CĂN HỘ 8X RAINBOW - NƠI GIỚI TRẺ LẬP NGHIỆP', '<p style="text-align: justify;"><strong><span style="color: #800000;">THE 8X RAINBOW</span></strong> tạo n&ecirc;n luồng kh&ocirc;ng kh&iacute; mới cho quận b&igrave;nh t&acirc;n.Con đường Binh Long trể n&ecirc;n s&ocirc;i động khi căn hộ đi vao hoạt động.Tạo n&ecirc;n c&acirc;n bằng d&acirc;n số tri thức cho khu B&igrave;nh T&acirc;n.C&oacute; thể n&oacute;i Rainbow sẽ hướng cho c&aacute;c gia đ&igrave;nh trẻ một cuộc sống mở ở nơi s&agrave;i g&ograve;n phồn hoa.&nbsp;</p>', '<p><strong>8X ĐẦM SEN</strong><span class="Apple-converted-space">&nbsp;</span>ra đời nhằm kiến tạo chốn an cư l&yacute; tưởng v&agrave; nền tảng vững chắc để c&aacute;c bạn trẻ tiến xa hơn tr&ecirc;n con đường sự nghiệp, bước đệm cho một cuộc sống thăng hoa. Với xu thế tự lập của tuổi trẻ thế hệ 8X, c&aacute;c căn hộ của dự &aacute;n <strong>8X ĐẦM SEN</strong><span class="Apple-converted-space">&nbsp;</span>sẽ đ&aacute;p ứng một c&aacute;ch tốt nhất cho cuộc sống của thời đại.</p>\r\n<p>Sự trẻ trung, năng động l&agrave; nguồn cảm hứng chủ đạo x&acirc;y dựng n&ecirc;n <strong>8X ĐẦM SEN</strong>. Cuộc sống như mơ nay đ&atilde; trở th&agrave;nh hiện thực v&agrave; Bạn sẽ thật sự tự h&agrave;o khi sở hữu n&oacute; trong tầm tay.</p>\r\n<p><strong>QUI M&Ocirc; DỰ &Aacute;N CĂN HỘ 8X ĐẦM SEN</strong></p>\r\n<p>Diện t&iacute;ch s&agrave;n x&acirc;y dựng:<span class="Apple-converted-space">&nbsp;</span><strong>56.773m2</strong><br />Diện t&iacute;ch khu&ocirc;n vi&ecirc;n:<span class="Apple-converted-space">&nbsp;</span><strong>6.188m2</strong><br />Diện t&iacute;ch x&acirc;y dựng:<span class="Apple-converted-space">&nbsp;</span><strong>2.494m2</strong><br />Mật độ x&acirc;y dựng:<span class="Apple-converted-space">&nbsp;</span><strong>40.3%</strong><br />Hệ số sử dụng đất:<strong><span class="Apple-converted-space">&nbsp;</span>7,4</strong><br />Số Block:<span class="Apple-converted-space">&nbsp;</span><strong>1</strong><br />Số tầng:<strong><span class="Apple-converted-space">&nbsp;</span>18 tầng</strong><br />Chiều cao x&acirc;y dựng:<span class="Apple-converted-space">&nbsp;</span><strong>75m</strong><br />Số căn hộ:<strong><span class="Apple-converted-space">&nbsp;</span>594 căn</strong></p>\r\n<p><strong><img class="aligncenter size-full wp-image-633" src="http://hungthinhland.biz/wp-content/uploads/2016/04/thu_moi3.jpg" sizes="(max-width: 800px) 100vw, 800px" srcset="http://hungthinhland.biz/wp-content/uploads/2016/04/thu_moi3.jpg 800w, http://hungthinhland.biz/wp-content/uploads/2016/04/thu_moi3-238x300.jpg 238w, http://hungthinhland.biz/wp-content/uploads/2016/04/thu_moi3-768x970.jpg 768w" alt="thu_moi(3)" width="800" height="1010" /></strong></p>', 1, '2016-11-11 07:26:39', 'phoi_canh_the_rainbow.jpg', 0, 0, 0, 'Liên Hệ', '<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Dự &aacute;n</span>&nbsp;<a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><span style="font-size: 12pt;"><strong><span style="color: #800000;">Căn hộ 8x rainbow</span></strong></span>&nbsp;</a><span style="color: #000000; font-size: 12pt;">Tọa lạc tại quận B&igrave;nh T&acirc;n c&oacute; gi&aacute; b&aacute;n chỉ 14,3tr/m2 với gi&aacute; thấp nhưng chất lượng cao khẳng định &nbsp;ph&acirc;n kh&uacute;c thị trường hấp dẫn dễ d&agrave;ng thu h&uacute;t người mua.</span></div>\r\n<div>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&ndash; Trung t&acirc;m Q. B&igrave;nh T&acirc;n gi&aacute;p ranh Q. T&acirc;n Ph&uacute;. Ngay ng&atilde; tư Bốn X&atilde;</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&ndash; C&aacute;ch TTTP khoảng 20 ph&uacute;t, c&aacute;ch TTVH Đầm Sen khoảng 5 ph&uacute;t, c&aacute;ch UBND Q. T&acirc;n Ph&uacute; khoảng 3 ph&uacute;t&hellip;</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&ndash; MT đường B&igrave;nh Long (30m), th&ocirc;ng ra đường H&ograve;a B&igrave;nh, Phan Anh, Thoại Ngọc Hầu</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&ndash; Li&ecirc;n kết với c&aacute;c quận l&acirc;n cận như: Q11, Q10, Q.T&acirc;n Ph&uacute;</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">&ndash; Gần chợ đầu mối T&acirc;n Hương.</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Căn hộ 8x Rainbow c&oacute; vị tr&iacute; kh&aacute; thuận tiện về giao th&ocirc;ng, gần một số trường học, chợ, si&ecirc;u thị co-opMart, Big C Nguyễn Sơn v&agrave; chỉ c&aacute;ch trung t&acirc;m TPHCM 8km</span></p>\r\n</div>\r\n<div><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/phoi-canh-rainbow.jpg?1470975109491" width="100%" /></a></div>\r\n<div>&nbsp;</div>\r\n<div style="text-align: justify;"><strong><span style="color: #800000;"><span style="font-size: 12pt;"><a style="color: #800000;" href="../duan-can-ho-8x-rainbow-chi-5-phut-den-cong-vien-van-hoa-dam-sen-4.html">Căn hộ 8X&nbsp;Rainbow</a></span>&nbsp;</span></strong><span style="color: #000000; font-size: 12pt;">l&agrave; một sản phẩm căn hộ 8x tiếp nối c&aacute;c dự &aacute;n th&agrave;nh c&ocirc;ng của tập đo&agrave;n Hưng Thịnh trong ph&acirc;n kh&uacute;c thị trường căn hộ trung b&igrave;nh cho giới trẻ. Căn hộ Rainbown nằm trong ph&acirc;n kh&uacute;c gi&aacute; thị trường d&agrave;nh cho kh&aacute;ch h&agrave;ng c&oacute; tầm t&agrave;i ch&iacute;nh vừa phải nhưng vẫn c&oacute; khả năng sở hữu căn hộ tiện nghi hiện đại v&agrave; thoải m&aacute;i tại trung t&acirc;m th&agrave;nh phố.</span></div>\r\n<div>\r\n<div>\r\n<p style="text-align: justify;"><span style="color: #800000; font-size: 12pt;"><strong>Căn hộ 8X&nbsp;Rainbow</strong></span>&nbsp;<span style="color: #000000; font-size: 12pt;">được x&acirc;y dựng tr&ecirc;n khu&ocirc;n đất rộng 5.123,5 m2 với mật độ x&acirc;y dựng chỉ 40%, điều n&agrave;y đồng nghĩa với bạn sẽ sống trong một m&ocirc;i trường trong xanh tươi m&aacute;t ngay tại kh&ocirc;ng gian sống được t&iacute;ch hợp trong căn hộ.</span></p>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 8X RAINBOW&nbsp;</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>VỊ TR&Iacute; CĂN HỘ 8X&nbsp;RAINBOW</strong></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;"><strong><span style="color: #800000;"><a style="color: #800000;" href="../duan-can-ho-8x-rainbow-chi-5-phut-den-cong-vien-van-hoa-dam-sen-4.html">8X Rainbown</a></span></strong></span>&nbsp;<span style="color: #000000; font-size: 12pt;">bật l&ecirc;n giữa khu T&acirc;y th&agrave;nh phố với lợi thế nổi trội ngay mặt tiền đường B&igrave;nh Long lộ giới 30m, con đường th&ocirc;ng tho&aacute;ng thuận tiện di chuyển đến quận 10, quận 11, quận 5 v&agrave; khu trung t&acirc;m chỉ mất 20 ph&uacute;t bằng c&aacute;c tuyến đường:</span></p>\r\n</div>\r\n<div>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/vi-tri-rainbow.png?1470975144395" width="100%" /></a></p>\r\n</div>\r\n<div>\r\n<p>&nbsp;</p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/tien-ich-rainbow.jpg?1470975211045" width="100%" /></a></p>\r\n</div>\r\n<div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mat-bang-rainbow.jpg?1470975244155" width="100%" /></a></div>\r\n<p>&nbsp;</p>\r\n<div style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 8X RAINBOW</strong></span>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>\r\n</div>\r\n</div>', '<div>\r\n<p style="text-align: justify;"><span style="font-size: 12pt; color: #000000;"><strong>VỊ TR&Iacute; CĂN HỘ 8X&nbsp;RAINBOW TIỆN LỢI CHO MỌI ĐIỂM ĐẾN</strong></span></p>\r\n<p style="text-align: justify;"><span style="font-size: 12pt;"><strong><span style="color: #800000;">8X Rainbown</span></strong><span style="color: #000000;">&nbsp;bật l&ecirc;n giữa khu T&acirc;y th&agrave;nh phố với lợi thế nổi trội ngay mặt tiền đường B&igrave;nh Long lộ giới 30m, con đường th&ocirc;ng tho&aacute;ng thuận tiện di chuyển đến quận 10, quận 11, quận 5 v&agrave; khu trung t&acirc;m chỉ mất 20 ph&uacute;t bằng c&aacute;c tuyến đường:</span></span></p>\r\n</div>\r\n<div>\r\n<ul>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Sở hữu c&aacute;c tuyền đường lớn&nbsp;C&aacute;ch mạng th&aacute;ng 8- Lạc Long Qu&acirc;n- Thoại Ngọc Hầu,&nbsp;&nbsp;Đường 3/2 -&nbsp; L&atilde;nh Binh Thăng - H&ograve;a B&igrave;nh,&nbsp;</span></li>\r\n<li><span style="color: #000000; font-size: 12pt;">Với những kh&aacute;ch h&agrave;ng l&agrave;m việc tại trung t&acirc;m quận 12, quận G&ograve; Vấp, T&acirc;n B&igrave;nh, B&igrave;nh Thạnh, Ph&uacute; Nhuận di chuyển đến dự &aacute;n chỉ mất 20 ph&uacute;t với c&aacute;c tuyến đường ch&iacute;nh:</span>\r\n<ul>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Trường Chinh- &Acirc;u Cơ- Lũy B&aacute;n B&iacute;ch- Thoại Ngọc Hầu</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Trường Chinh- T&acirc;n Kỳ T&acirc;n Qu&yacute;- B&igrave;nh Long</span></li>\r\n<li style="text-align: justify;"><span style="color: #000000; font-size: 12pt;">Trường Chinh- T&acirc;n Sơn Nh&igrave;- T&acirc;n Hương &ndash; Văn Cao &ndash; B&igrave;nh Long</span></li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/vi-tri-rainbow_1.png?1470975512668" width="100%" /></a></p>\r\n<h4 style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 8X RAINBOW</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>', '<div><span style="font-size: 12pt; color: #000000;"><strong>TIỆN &Iacute;CH VƯỢT TRỘI TẠI CĂN HỘ 8X RAINBOW:</strong></span></div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<div style="text-align: justify;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;<span style="color: #000000; font-size: 12pt;">Căn hộ 8X RainBow&nbsp;được trang bị hệ thống 5 thang m&aacute;y d&agrave;nh cho cư d&acirc;n, 1 thang m&aacute;y d&agrave;nh ri&ecirc;ng cho trường hợp đặc biệt, 2 thang bộ v&agrave; một cầu thang ngo&agrave;i.</span></div>\r\n<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;C&oacute; nh&agrave;&nbsp;Nh&agrave; trẻ, khu mua sắm, cafe, spa,&nbsp;ph&ograve;ng tập thể dục hiện đại.</span></div>\r\n<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;Tuyệt vời&nbsp;hơn khi Rainbow được x&acirc;y dựng theo một ph&ograve;ng c&aacute;ch mở&nbsp;c&oacute; c&ocirc;ng vi&ecirc;n c&acirc;y xanh, hồ nước. Khu vui chơi giải tr&iacute; cho trẻ em... rộng r&atilde;i, sạch đẹp v&agrave; tho&aacute;ng m&aacute;t.</span></div>\r\n</div>\r\n<div><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/tien-ich-rainbow_1.jpg?1470975769041" width="100%" /></a></div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div style="text-align: justify;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;<span style="color: #000000; font-size: 12pt;">Camera c&oacute; trong hầm xe, thang m&aacute;y, v&agrave; h&agrave;nh lang từng tầng</span></div>\r\n<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;Hệ thống cấp nước, tho&aacute;t nước, cấp điện: hệ thống của th&agrave;nh phố.</span></div>\r\n<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;Hệ thống b&aacute;o ch&aacute;y, b&aacute;o kh&oacute;i: tự động trong từng căn hộ, v&ograve;i phun nước v&agrave; b&igrave;nh chữa ch&aacute;y được trang bị ngay h&agrave;nh lang mỗi tầng</span></div>\r\n<div style="text-align: justify;"><span style="color: #000000; font-size: 12pt;"><img src="http://sanhungthinh.com/upload/images/next-icon.png" alt="" />&nbsp;Hệ thống an ninh được bảo vệ 24/24 bởi Cty quản l&yacute; t&ograve;a nh&agrave; chuy&ecirc;n nghiệp.</span></div>\r\n<div>\r\n<h4 style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 8X RAINBOW&nbsp;</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n</div>', '<p><span style="font-size: 12pt; color: #000000;"><strong>MẶT BẰNG CĂN HỘ 8X RAINBOW</strong></span></p>\r\n<p style="text-align: justify;"><span style="color: #800000; font-size: 12pt;"><strong>Căn hộ Rainbow</strong> </span><span style="color: #000000; font-size: 12pt;">được&nbsp;thiết kế th&ocirc;ng minh nhằm tạo n&ecirc;n kh&ocirc;ng gian sống sang trọng nhưng vẫn h&agrave;i h&ograve;a c&ugrave;ng thi&ecirc;n nhi&ecirc;n, với mong muốn&nbsp;phục vụ mọi đối tượng gia đ&igrave;nh v&agrave; nhu cầu sử dụng căn hộ.</span></p>\r\n<p><span style="font-size: 12pt; color: #000000;">Căn hộ&nbsp;được thiết kế theo chuẩn <strong>Căn hộ c&oacute;&nbsp;2&nbsp;ph&ograve;ng ngủ</strong>&nbsp;v&agrave;&nbsp;<strong>Căn hộ c&oacute; 3 ph&ograve;ng ngủ</strong>&nbsp;với diện t&iacute;ch từ 63.83-&gt;92.66&nbsp;m2.&nbsp;</span></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mo-hinh-rainbow.jpg?1470976026552" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mat-bang-rainbow_1.jpg?1470976062359" width="100%" /></a></p>\r\n<p>&nbsp;<span style="font-size: 12pt; color: #000000;"><strong>Căn A: 63,83M2 ( 2 PH&Ograve;NG NGŨ, 2 VỆ SINH )</strong></span></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mat-bang-rainbow-1.jpg?1470976108719" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>Căn hộ B : 82,89M2 ( 2 PH&Ograve;NG NGŨ, 2 VỆ SINH )</strong></span></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mat-bang-rainbow-2.jpg?1470976145995" width="100%" /></a></p>\r\n<p><span style="font-size: 12pt; color: #000000;"><strong>Căn Hộ C 82,62m2 ( 3 PH&Ograve;NG NGŨ, 2 VỆ SINH )</strong></span></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/mat-bang-rainbow-3.jpg?1470976188221" width="100%" /></a></p>\r\n<p>&nbsp;<span style="font-size: 12pt; color: #000000;"><strong>Căn Hộ D 92,66m2 ( 3 PH&Ograve;NG NGŨ, 2 VỆ SINH )</strong></span></p>\r\n<h4 style="text-align: center;">&nbsp;</h4>\r\n<h4 style="text-align: center;"><span style="font-size: 14pt; color: #ff0000;"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; 8X RAINBOW</strong></span></h4>\r\n<h4><strong><img style="display: block; margin-left: auto; margin-right: auto;" src="http://sanhungthinh.com/upload/images/animated-arrow.gif" alt="" width="47" height="26" /></strong></h4>\r\n<h4><a href="#dangkyxemnha"><img style="display: block; margin-left: auto; margin-right: auto;" src="../media/images/dang-ky-button.png?1466180235102" width="200" height="76" /></a></h4>\r\n<h4 style="text-align: center;">&nbsp;</h4>', '', '<p>&nbsp;<span style="color: #000000;"><span style="font-size: 12pt;">Một số h&igrave;nh ảnh từ nh&agrave; mẫu cưa <strong><span style="color: #800000;">8X RAINBOW</span></strong></span></span></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/nha-mau-rainbow-1.jpg?1470976350650" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/nha-mau-rainbow-2.jpg?1470976381826" width="100%" /></a></p>\r\n<p><a href="../duan-can-ho-8x-rainbow-noi-gioi-tre-lap-nghiep-4.html"><img src="../media/images/nha-mau-rainbow-3.jpg?1470976416218" width="100%" /></a></p>\r\n<p style="text-align: center;">&nbsp;<span style="font-size: 14pt; color: #