INSERT INTO `introduction` (`id`, `content`, `is_actived`) VALUES
(4, '<div class="left_pannel">\r\n<h1 class="titleMain"><span style="color: #000000;">GIỚI THIỆU</span></h1>\r\n<div class="showtext">\r\n<table style="width: 100%;" border="0" cellspacing="0" cellpadding="0" align="left">\r\n<tbody>\r\n<tr>\r\n<td style="width: 355px;"><img src="../media/images/nguyen-dinh-trung.jpg?1473236573786" width="300" height="350" /><br />\r\n<div>\r\n<p><span style="color: #000000;">&Ocirc;ng Nguyễn Đ&igrave;nh Trung</span><br /><span style="color: #000000;">Chủ tịch HĐQT ki&ecirc;m Tổng Gi&aacute;m Đốc</span></p>\r\n</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p style="text-align: justify;"><span style="color: #800000;"><a style="color: #800000;" href="../"><strong>HUNG THINH CORP</strong></a></span>&nbsp;<span style="color: #000000;">được th&agrave;nh lập v&agrave; ph&aacute;t triển với gần 15 năm kinh nghiệm trong lĩnh vực bất động sản. Với chiến lược ph&ugrave; hợp v&agrave; tư duy quản trị đ&uacute;ng đắn c&ugrave;ng tinh thần đo&agrave;n kết, nỗ lực của to&agrave;n c&ocirc;ng ty, thương hiệu Hưng Thịnh ng&agrave;y c&agrave;ng được khẳng định vững chắc tr&ecirc;n thị trường v&agrave; kh&ocirc;ng ngừng ph&aacute;t triển th&agrave;nh một c&ocirc;ng ty bất động sản uy t&iacute;n tại thị trường ph&iacute;a Nam n&oacute;i ri&ecirc;ng v&agrave; cả nước n&oacute;i chung.</span><br /><br /><span style="color: #800000;"><strong>HUNG THINH CORP</strong></span>&nbsp;<span style="color: #000000;">đ&atilde; ho&agrave;n th&agrave;nh lộ tr&igrave;nh t&aacute;i cấu tr&uacute;c bộ m&aacute;y hoạt động theo m&ocirc; h&igrave;nh Tổng C&ocirc;ng ty v&agrave; c&aacute;c đơn vị th&agrave;nh vi&ecirc;n. Hiện nay, c&ocirc;ng ty kh&ocirc;ng ngừng lớn mạnh về quy m&ocirc; với 07 đơn vị th&agrave;nh vi&ecirc;n, 01 văn ph&ograve;ng đại diện v&agrave; c&aacute;c chi nh&aacute;nh hoạt động chuy&ecirc;n nghiệp, hiệu quả trong c&aacute;c lĩnh vực: đầu tư, thiết kế, x&acirc;y dựng, sản xuất nội thất v&agrave; ph&acirc;n phối dự &aacute;n bất động sản, tạo th&agrave;nh quy tr&igrave;nh kh&eacute;p k&iacute;n nhằm cung cấp g&oacute;i giải ph&aacute;p to&agrave;n diện cho Đối t&aacute;c v&agrave; mang đến cho Kh&aacute;ch h&agrave;ng những sản phẩm chất lượng với mức gi&aacute; hợp l&yacute;.</span><br /><br /><span style="color: #000000;">Theo chiến lược ph&aacute;t triển trong những năm tới</span>,&nbsp;<span style="color: #800000;"><strong>HUNG THINH CORP</strong></span>&nbsp;<span style="color: #000000;">tiếp tục định hướng ph&aacute;t triển bất động sản l&agrave; lĩnh vực nền tảng cho mọi hoạt động kinh doanh, đầu tư bất động sản để t&iacute;ch lũy cho tương lai, tạo mức tăng trưởng ổn định, bền vững v&agrave; khẳng định sức mạnh to&agrave;n diện của</span>&nbsp;<span style="color: #800000;"><a style="color: #800000;" href="../"><strong>HUNG THINH</strong></a></span>. <span style="color: #000000;">Th&ocirc;ng qua việc ưu ti&ecirc;n khai th&aacute;c những thế mạnh v&agrave; năng lực cốt l&otilde;i sẵn c&oacute; để mở rộng hợp t&aacute;c đầu tư, Hưng Thịnh cam kết mang đến những gi&aacute; trị tốt đẹp cho Đối t&aacute;c v&agrave; Kh&aacute;ch h&agrave;ng, g&oacute;p phần x&acirc;y dựng thị trường bất động sản chuy&ecirc;n nghiệp, l&agrave;nh mạnh.</span></p>\r\n<p style="text-align: justify;"><span style="color: #000000;">Tr&ecirc;n h&agrave;nh tr&igrave;nh ph&aacute;t triển của m&igrave;nh, ch&uacute;ng t&ocirc;i nhận thức rằng, để th&agrave;nh c&ocirc;ng v&agrave; ph&aacute;t triển bền vững, b&ecirc;n cạnh định hướng chiến lược đ&uacute;ng đắn, th&igrave; việc tr&acirc;n trọng gi&aacute; trị đạo đức kinh doanh, mang đến sản phẩm thực, gi&aacute; trị thực, đảm bảo lợi &iacute;ch tốt nhất cho Kh&aacute;ch h&agrave;ng, Đối t&aacute;c v&agrave; x&atilde; hội l&agrave; triết l&yacute; kinh doanh đầy t&iacute;nh nh&acirc;n văn m&agrave; c&ocirc;ng ty ch&uacute;ng t&ocirc;i lu&ocirc;n hướng đến.</span><br /><br /><span style="color: #000000;">Với l&ograve;ng quyết t&acirc;m v&agrave; sự nỗ lực kh&ocirc;ng ngừng của Hội đồng Quản trị, Ban điều h&agrave;nh v&agrave; to&agrave;n thể c&aacute;n bộ - nh&acirc;n vi&ecirc;n,&nbsp;</span><br /><span style="color: #800000;"><strong>HUNG THINH CORP</strong>&nbsp;</span><span style="color: #000000;">sẽ lu&ocirc;n đem đến cho thị trường những dự &aacute;n đầy tiềm năng v&agrave; l&agrave; người đồng h&agrave;nh tr&ecirc;n h&agrave;nh tr&igrave;nh đi đến sự thịnh vượng v&agrave; sung t&uacute;c của Đối t&aacute;c v&agrave; Kh&aacute;ch h&agrave;ng.</span></p>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n<div style="text-align: center;"><span style="color: #000000;">Tr&acirc;n trọng</span></div>\r\n<div style="text-align: center;"><span style="color: #000000;">NGUYỄN Đ&Igrave;NH TRUNG</span><br /><span style="color: #000000;">CHỦ TỊCH HĐQT &ndash; TGĐ HUNG THINH CORP</span></div>\r\n<div style="text-align: center;">&nbsp;</div>\r\n</div>\r\n</div>', 1);

