INSERT INTO `duan` VALUES ('1', 'SAIGONMIA TRUNG SƠN - SKY IN YOUR EYES', '<p style=\"text-align: justify;\"><strong><span style=\"color: #800000;\"><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SAI GON MIA TRUNG SƠN</a> </span></strong>hiện hữu tr&ecirc;n khu&ocirc;n đất v&agrave;ng Trung Sơn.L&agrave; nơi được vinh danh của sự gi&agrave;u sang v&agrave; hưởng thụ.Nơi đ&acirc;y Bạn c&oacute; kh&ocirc;ng gian ri&ecirc;ng cua bầu trời,của sự tinh hoa s&ocirc;ng nước thi&ecirc;n cảnh.S&agrave;i G&ograve;n Mia c&aacute;i t&ecirc;n n&oacute;i l&ecirc;n tất cả</p>', '1', '2017-01-17 16:05:26', 'saigonmia_pc.jpg', '<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><span style=\"color: #800000;\"><strong><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a></strong>&nbsp;<span style=\"color: #000000;\">khẳng định th&ecirc;m gi&aacute; trị sống của khu Trung Sơn vốn m&ecirc;nh danh khu nh&agrave; gi&agrave;u của Sai G&ograve;n.Kh&ocirc;ng phải hiển nhi&ecirc;n m&agrave; giới thượng lưu tập trung nơi đ&acirc;y sinh s&ocirc;ng,một miền đất hứa với phong thủy s&ocirc;ng nước bao quanh.Trung Sơn với ba mặt tiền s&ocirc;ng v&agrave; chỉ 1 con đường dẫn v&agrave;o tạo n&ecirc;n sự kh&aacute;c biết cho S&agrave;i G&ograve;n Mia.B&ecirc;n cạnh yếu tố địa h&igrave;nh th&igrave; phần lớn d&acirc;n cư sinh sống ở đ&acirc;y đều giới kinh doanh si&ecirc;u gi&agrave;u c&oacute;,giới tri thức cao,họ sống ở đ&acirc;y v&igrave; đăng cấp,v&igrave; một gia đ&igrave;nh danh gi&aacute;.</span></span>&nbsp;<strong><span style=\"color: #800000;\"><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a>&nbsp;</span></strong><span style=\"color: #800000;\"><span style=\"color: #000000;\">hiện hữu giữa rừng hoa như vậy kh&ocirc;ng thể kem cạnh được,chủ đầu tư muốn SaiGonMia l&agrave; vi&ecirc;n ngọc s&aacute;ng nhất ở Trung sơn v&agrave;&nbsp;</span></span><span style=\"color: #000000;\">sẽ đem đến cho gia đ&igrave;nh bạn cuộc sống trong l&agrave;nh v&agrave; đẳng cấp nhất.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\">&nbsp;</span></p>\r\n<p><a href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><img src=\"../media/images/sai-gon-mia.jpg?1470885868294\" width=\"100%\" /></a></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 10pt;\"><span style=\"color: #800000;\"><span style=\"font-size: 12pt;\"><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><span style=\"color: #800000;\"><strong>S&agrave;i G&ograve;n Mia</strong></span>&nbsp;</a></span>&nbsp;<span style=\"color: #000000;\"><span style=\"font-size: 12pt;\">nằm trong quần thể c&aacute;c kiến tr&uacute;c nh&agrave; phố hiện đại,được thiết kế c&ocirc;ng phu,h&ograve;a chung với lu&ocirc;ng kiến tr&uacute;c đ&oacute;,một kiến tr&uacute;c sang trọng theo thời gian được <span style=\"color: #800000;\"><strong><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaiGonMia</a></strong> </span>tạo n&ecirc;n.</span></span></span><span style=\"font-size: 12pt;\">&nbsp;<span style=\"color: #000000;\">Tọa lạc tại vị tr&iacute; đắc địa nhất của khu vực Nam S&agrave;i G&ograve;n, ngo&agrave;i việc được thừa hưởng những th&agrave;nh quả hạ tầng x&atilde; hội v&agrave; tiện t&iacute;ch&nbsp;sẵn c&oacute; của&nbsp;Mia&nbsp;Trung Sơn hứa hẹn dự &aacute;n sẽ l&agrave; nơi an cư l&yacute; tưởng mọi cư d&acirc;n S&agrave;i G&ograve;n Mia.</span></span></span></p>\r\n<p><span style=\"font-size: 10pt;\">&nbsp;</span></p>\r\n<hr />\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 10pt;\">Nếu quan t&acirc;m dự &aacute;n,bạn nhớ để th&ocirc;ng tin tại đ&acirc;y nh&eacute;,Chuy&ecirc;n vi&ecirc;n tư vấn Hưng Thịnh sẽ li&ecirc;n hệ bạn sớm nhất,mọi th&ocirc;ng tin tương t&aacute;c giữa bạn v&agrave; ch&uacute;ng t&ocirc;i đều l&agrave; th&ocirc;ng tin mở.Mọi quyết định l&agrave; ở bạn,ch&uacute;ng t&ocirc;i chỉ cung cấp nhưng th&ocirc;ng tin bạn cần.Tr&acirc;n Trọng Cảm Ơn</span></p>\r\n<h4 style=\"text-align: center;\">&nbsp;</h4>\r\n<h4 style=\"text-align: center;\"><span style=\"color: #800000; font-size: 14pt;\"><strong><span style=\"color: #ff0000;\">ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</span></strong>&nbsp;</span></h4>\r\n<h4 style=\"text-align: center;\"><span style=\"color: #800000; font-size: 14pt;\"><span style=\"color: #ff0000;\"><strong><img src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"43\" height=\"24\" /></strong></span></span><a href=\"#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: 10pt;\"><strong><span style=\"color: #000000;\">S&agrave;i G&ograve;n Mia sở hữu tiện &iacute;ch nội khu vượt trội</span>&nbsp;</strong></span></p>\r\n<p>&nbsp;</p>\r\n<p><strong><a href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><img src=\"../media/images/tien-ich.png?1470885949851\" width=\"100%\" /><img src=\"../media/images/tien-ich-3.png?1470886467399\" width=\"100%\" /><img src=\"../media/images/tien-ich-4.png?1470886506015\" width=\"100%\" /></a></strong><br /><br /></p>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 10pt;\">Nếu quan t&acirc;m dự &aacute;n,bạn nhớ để th&ocirc;ng tin tại đ&acirc;y nh&eacute;,Chuy&ecirc;n vi&ecirc;n tư vấn Hưng Thịnh sẽ li&ecirc;n hệ bạn sớm nhất,mọi th&ocirc;ng tin tương t&aacute;c giữa bạn v&agrave; ch&uacute;ng t&ocirc;i đều l&agrave; th&ocirc;ng tin mở.Mọi quyết định l&agrave; ở bạn,ch&uacute;ng t&ocirc;i chỉ cung cấp nhưng th&ocirc;ng tin bạn cần.Tr&acirc;n Trọng Cảm Ơn</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<h4 style=\"text-align: center;\"><strong><span style=\"font-size: 14pt; color: #ff0000;\">ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA</span>&nbsp;</strong>&nbsp;</h4>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"49\" height=\"27\" /></strong><a href=\"../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>\r\n<p>&nbsp;</p>\r\n<h4>&nbsp;</h4>\r\n<h4 style=\"text-align: center;\"><a href=\"#dangkyxemnha\">&nbsp;</a></h4>\r\n<p><span style=\"color: #000000; font-size: 12pt;\"><strong>TỔNG QUAN DỰ &Aacute;N</strong></span></p>\r\n<div><span style=\"color: #000000;\">&nbsp;&nbsp;<span style=\"font-size: 10pt;\">&nbsp;<span style=\"font-size: 12pt;\"><strong>Vị tr&iacute;:</strong>&nbsp;Đường 9A, Khu d&acirc;n cư Trung Sơn, TP. HCM&nbsp;</span></span></span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch khu đất:</strong>&nbsp;15.126m<sup>2</sup>&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối đế:</strong>&nbsp;46,46%&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối th&aacute;p:&nbsp;</strong>33,70%&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Tổng số căn hộ:</strong>&nbsp;869 căn&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch căn hộ:</strong>&nbsp;49,83m<sup>2</sup>&nbsp;&ndash; 83,05m&nbsp;<sup>2</sup>&nbsp;</span></div>\r\n<div><span style=\"color: #000000; font-size: 12pt;\">&nbsp;</span></div>\r\n<div><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Số block:</strong>&nbsp;03 block&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Số tầng:</strong>&nbsp;27 tầng nổi, 02 tầng hầm</span></div>\r\n<div><span style=\"color: #000000; font-size: 12pt;\">&nbsp;</span><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Trung t&acirc;m thương mại:</strong>&nbsp;04 tầng&nbsp;</span></div>\r\n<div><br /><span style=\"color: #000000; font-size: 12pt;\">&nbsp;&nbsp;&nbsp;<strong>Loại h&igrave;nh:</strong>&nbsp;Căn hộ, căn trệt thương mại, office-tel, TTTM</span></div>', '<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; color: #000000;\"><strong>TẠI KHU TRUNG SƠN - NAM S&Agrave;I G&Ograve;N</strong></span><br /><br /><span style=\"font-size: 10pt;\"><span style=\"font-size: 12pt;\"><a href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><span style=\"color: #800000;\"><strong>SaigonMia</strong></span>&nbsp;</a></span>&nbsp;<span style=\"color: #000000; font-size: 12pt;\">l&agrave; một trong nhưng dự &aacute;n tầm cao hiện đại bậc nhất tạo lạc tại khu Trung Sơn.Từ con đường Nguyễn Văn Cừ nối d&agrave;i đi v&agrave;o bạn se cảm nhận sự kh&aacute;c biệt của miền đất nơi đ&acirc;y,với ba mặt view s&ocirc;ng&nbsp;.Trong qu&aacute; tr&igrave;nh hội nhập,c&aacute;c th&agrave;nh phố đang qu&aacute; tải về mặt con người,rất kh&oacute; để c&oacute; được một nơi như khu Trung Sơn.Giữa trung t&acirc;m th&agrave;nh thị m&agrave; sở hưu khu đất gần gũi với thi&ecirc;n nhi&ecirc;n như vậy.Nhờ sở hữu vị tr&iacute; l&yacute; tưởng n&ecirc;n </span><span style=\"font-size: 12pt; color: #800000;\"><strong><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a></strong></span>&nbsp;<span style=\"color: #000000; font-size: 12pt;\">t&aacute;ch biệt vừa đủ để tr&aacute;nh ồn &agrave;o nơi phố thị nhưng vẫn đem lại cho cư d&acirc;n cuộc sống tiện nghi v&agrave; gần gũi thi&ecirc;n nhi&ecirc;n.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">&nbsp;</span></p>\r\n<div>\r\n<p><a href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><img src=\"../media/images/vi-tri.jpg?1470886837178\" width=\"100%\" /></a></p>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p style=\"text-align: justify;\"><br /><span style=\"font-size: 10pt;\"><span style=\"font-size: 12pt; color: #000000;\">Từ</span> <span style=\"color: #800000;\"><span style=\"font-size: 12pt;\"><strong><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a>&nbsp;</strong></span><span style=\"color: #000000; font-size: 12pt;\">bạn c&oacute; thể di chuyển tới trung t&acirc;m th&agrave;nh phố một c&aacute;ch nhanh nhất tr&ecirc;n những con đường xanh m&aacute;t.C</span></span><span style=\"color: #000000; font-size: 12pt;\">ư d&acirc;n chỉ mất khoảng 5 ph&uacute;t để v&agrave;o trung t&acirc;m Quận 1 v&agrave; Khu đ&ocirc; thị Ph&uacute; Mỹ Hưng, đồng thời dễ d&agrave;ng tận hưởng những tiện &iacute;ch h&agrave;ng đầu chỉ trong b&aacute;n k&iacute;nh 2km.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 10pt;\">&nbsp;</span></p>\r\n<p>&nbsp;<img src=\"../media/images/khoang-cach.png?1470886903603\" width=\"100%\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h4 style=\"text-align: center;\"><span style=\"font-size: 14pt; color: #ff0000;\"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; SAIGON MIA&nbsp;</strong>&nbsp;</span></h4>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"47\" height=\"26\" /></strong><a href=\"../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>', '<p><a href=\"../duan-saigonmia-sky-in-yo