INSERT INTO `duan` VALUES ('6', 'MELODY RESIDENCES TÂN BÌNH - MẶT TIỀN ĐƯỜNG ÂU CƠ', '<p style=\"text-align: justify;\"><strong><span style=\"color: #800000;\">MELODY RESIDENCES</span></strong> tọa lạc tr&ecirc;n mặt tiền &acirc;u cơ,l&agrave; cơ hội cho c&aacute;c gia đ&igrave;nh trẻ hội nhập cuộc sống ổn định ở TPHCM.Nằm ở vị tr&iacute; thuận lợi cho hướng ph&aacute;t triển c&ocirc;ng việc cua giới trẻ,c&oacute; th&ecirc; n&oacute;i sống ở đ&acirc;y bạn đam bảo cuộc sống thuận tiện nhất,một cơ hội mở ra cho tương lai trẻ thơ.</p>', '1', '2016-11-17 15:22:04', 'phoi_canh_melody.png', '<p><strong><span style=\"color: #800000; font-size: 12pt;\"><a style=\"color: #800000;\" href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\">CĂN HỘ MELODY RESIDENCES</a></span> <span style=\"font-size: 12pt; color: #000000;\">- <span style=\"color: #800000;\">&Acirc;U CƠ</span></span></strong></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><span style=\"color: #000000;\">Tổng thể</span> <strong><span style=\"color: #800000;\"><a style=\"color: #800000;\" href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\">MELODY RESIDENCES</a></span></strong></span><span style=\"color: #000000; font-size: 12pt;\"> trong quần thể kiến tr&uacute;c nh&agrave; phố nh&igrave;n từ tr&ecirc;n cao.Một khu căn hộ mọc l&ecirc;n chọc trời tạo n&ecirc;n khu phức hợp kh&eacute;p k&iacute;n cho khu d&acirc;n sinh sống nơi đ&acirc;y.Thị trường căn hộ ng&agrave;y c&agrave;ng n&oacute;ng l&ecirc;n với sự gia tăng về d&acirc;n số đổ về th&agrave;nh thị.</span></p>\r\n<p><a href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\"><img src=\"../media/images/phoi-canh-melody-residence.jpg?1470972657867\" width=\"100%\" /></a></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><span style=\"color: #000000;\">Với kiến tr&uacute;c s&aacute;ng tạo từ sự kết hợp h&agrave;i h&ograve;a giữa nhịp sống hiện đại c&ugrave;ng thi&ecirc;n nhi&ecirc;n xanh m&aacute;t trong l&agrave;nh</span>,</span>&nbsp;<span style=\"color: #800000; font-size: 12pt;\"><a style=\"color: #800000;\" href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\"><strong>Melody Residences</strong></a></span>&nbsp;<span style=\"color: #000000; font-size: 12pt;\">kh&ocirc;ng chỉ mang lại sự tiện nghi v&agrave; sang trọng m&agrave; c&ograve;n l&agrave; nơi l&yacute; tưởng để bạn thực hiện những ước mơ,l&agrave;m mới m&igrave;nh mỗi ng&agrave;y v&agrave; sẵn s&agrave;ng cho một ng&agrave;y mới tr&agrave;n đầy năng lượng.</span><span style=\"color: #800000; font-size: 12pt;\"><strong> <a style=\"color: #800000;\" href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\">Melody &Acirc;u Cơ</a></strong> </span><span style=\"color: #000000; font-size: 12pt;\">l&agrave; minh chứng cho sự t&iacute;ch hợp những tiện &iacute;ch tuyệt vời nhằm phục vụ tất cả mọi nhu cầu của mỗi cư d&acirc;n như: trung t&acirc;m thương mại, shop house, nh&agrave; trẻ, gym, spa&hellip;Với những tiện &iacute;ch ngay ngưỡng cửa, chỉ cần v&agrave;i bước ch&acirc;n, bạn c&oacute; thể thả m&igrave;nh trong l&agrave;n nước trong xanh m&aacute;t lạnh của hồ bơi hay thư gi&atilde;n c&ugrave;ng gia đ&igrave;nh v&agrave; bạn b&egrave; tại cafe tầng 3 của khu căn hộ.&nbsp;</span></p>\r\n<p><span style=\"color: #000000;\">&nbsp;</span></p>\r\n<p><a href=\"../duan-can-ho-melody-residences-mat-tien-duong-au-co-6.html\"><img src=\"../media/images/vi-tri-melody-residence.gif?1470972695032\" width=\"100%\" /></a></p>\r\n<div>\r\n<p style=\"text-align: center;\">&nbsp;<span style=\"font-size: 14pt; color: #ff0000;\"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute;&nbsp;MELODY &Acirc;U CƠ</strong></span></p>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"47\" height=\"26\" /></strong></h4>\r\n<h4><a href=\"#dangkyxem