INSERT INTO `duan` VALUES ('27', 'OFFICETEL SAIGON MIA - QUẬN 7', '<p style=\"text-align: justify;\"><span style=\"color: #800000;\"><strong>OFFICE-TEL SAIGON MIA</strong></span>&nbsp;tọa lạc tại khu Trung Sơn l&agrave; m&ocirc; h&igrave;nh văn ph&ograve;ng l&agrave;m việc kết hợp nơi nghỉ ngơi tiện nghi, c&oacute; diện t&iacute;ch từ 34m2 đến 60m2 ph&ugrave; hợp nhiều đối tượng kh&aacute;ch h&agrave;ng,nhằm tối ưu h&oacute;a c&ocirc;ng năng v&agrave; diện t&iacute;ch sử dụng.L&agrave; m&ocirc; h&igrave;nh đang được giới đầu tư săn đ&oacute;n trong thời gian qua.</p>', '2', '2016-11-07 09:42:59', 'hinh-anh-officetel-sky-1.jpg', '<div>\r\n<div>\r\n<p><span style=\"color: #000000; font-size: 12pt;\"><strong>CĂN HỘ VĂN PH&Ograve;NG OFFICETEL SAIGON MIA</strong></span></p>\r\n</div>\r\n</div>\r\n<div>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">M&ocirc; h&igrave;nh office-tel l&agrave; m&ocirc; h&igrave;nh văn ph&ograve;ng l&agrave;m việc kh&ocirc;ng giới hạn giờ giao dịch v&agrave; nơi nghỉ ngơi tiện nghi, c&oacute; diện t&iacute;ch từ 29m2 đến tr&ecirc;n 55m2 ph&ugrave; hợp nhiều đối tượng kh&aacute;ch h&agrave;ng, nhằm tối ưu h&oacute;a c&ocirc;ng năng v&agrave; diện t&iacute;ch sử dụng.</span></p>\r\n<p><a href=\"../duan-officetel-saigon-mia-quan-7-27.html\"><img src=\"../media/images/sai-gon-mia.jpg?1470885868294\" width=\"100%\" /></a></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">Điểm kh&aacute;c biệt nổi bật của office-tel nằm ở t&iacute;nh đa năng v&agrave; cung cấp được nhiều tiện &iacute;ch ph&ugrave; hợp với xu hướng sống năng động hiện nay.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">Văn ph&ograve;ng cao cấp: office-tel được định hướng ph&aacute;t triển đồng bộ v&agrave; chuy&ecirc;n nghiệp, cung cấp c&aacute;c tiện &iacute;ch văn ph&ograve;ng ti&ecirc;u chuẩn như lối v&agrave;o ri&ecirc;ng, sảnh lễ t&acirc;n, khu vực bảng t&ecirc;n c&ocirc;ng ty, hệ thống thang m&aacute;y d&agrave;nh ri&ecirc;ng cho kh&aacute;ch văn ph&ograve;ng, hệ thống viễn th&ocirc;ng. M&ocirc; h&igrave;nh n&agrave;y đảm bảo t&iacute;nh an to&agrave;n v&agrave; chuy&ecirc;n nghiệp trong một khu văn ph&ograve;ng hạng A với hệ thống kiểm so&aacute;t an ninh, thẻ an ninh v&agrave; hệ thống quản l&yacute; t&ograve;a nh&agrave; chuy&ecirc;n nghiệp.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt;\"><span style=\"color: #000000;\">Nơi nghỉ ngơi tiện nghi:&nbsp;ưu thế kh&aacute;c biệt so với văn ph&ograve;ng truyền thống, người sử dụng office-tel được ph&eacute;p nghỉ ngơi qua đ&ecirc;m, đăng k&yacute; tạm tr&uacute; v&agrave; được tận hưởng tất cả c&aacute;c tiện &iacute;ch của một khu căn hộ cao cấp như hồ bơi, ph&ograve;ng gym, nh&agrave; h&agrave;ng, khu mua sắm, chăm s&oacute;c sức</span> khỏe&hellip;</span></p>\r\n<p><span style=\"font-size: 12pt; color: #000000;\"><strong>VỊ TR&Iacute; CĂN HỘ VĂN PH&Ograve;NG OFFICETEL SAIGON MIA:</strong></span>&nbsp;</p>\r\n<p><a href=\"../duan-officetel-saigon-mia-quan-7-27.html\"><img src=\"../media/images/vi-tri.jpg?1470886837178\" width=\"100%\" /></a></p>\r\n<h4 style=\"text-align: center;\"><span style=\"font-size: 14pt; color: #ff0000;\"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; OFFICETEL</strong></span></h4>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"47\" height=\"26\" /></strong><a href=\"../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>\r\n</div>', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\"><strong>TẠI KHU TRUNG SƠN - NAM S&Agrave;I G&Ograve;N</strong></span><br /><span style=\"color: #800000; font-size: 12pt;\"><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\"><strong>SaigonMia</strong>&nbsp;</a></span><span style=\"color: #000000;\">&nbsp;<span style=\"font-size: 12pt;\">l&agrave; một trong nhưng dự &aacute;n tầm cao hiện đại bậc nhất tạo lạc tại khu Trung Sơn.Từ con đường Nguyễn Văn Cừ nối d&agrave;i đi v&agrave;o bạn se cảm nhận sự kh&aacute;c biệt của miền đất nơi đ&acirc;y,với ba mặt view s&ocirc;ng&nbsp;.Trong qu&aacute; tr&igrave;nh hội nhập,c&aacute;c th&agrave;nh phố đang qu&aacute; tải về mặt con người,rất kh&oacute; để c&oacute; được một nơi như khu Trung Sơn.Giữa trung t&acirc;m th&agrave;nh thị m&agrave; sở hưu khu đất gần gũi với thi&ecirc;n nhi&ecirc;n như vậy.Nhờ sở hữu vị tr&iacute; l&yacute; tưởng n&ecirc;n</span></span> <span style=\"color: #800000; font-size: 12pt;\"><strong><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a></strong></span><span style=\"color: #000000;\">&nbsp;<span style=\"font-size: 12pt;\">t&aacute;ch biệt vừa đủ để tr&aacute;nh ồn &agrave;o nơi phố thị nhưng vẫn đem lại cho cư d&acirc;n cuộc sống tiện nghi v&agrave; gần gũi thi&ecirc;n nhi&ecirc;n.</span></span></p>\r\n<div>\r\n<p><a href=\"../duan-officetel-saigon-mia-quan-7-27.html\"><img src=\"../media/images/vi-tri.jpg?1470886837178\" width=\"100%\" /></a></p>\r\n</div>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">Từ</span> <strong><span style=\"color: #800000; font-size: 12pt;\"><a style=\"color: #800000;\" href=\"../duan-saigonmia-sky-in-your-eyes-1.html\">SaigonMia</a></span>&nbsp;</strong><span style=\"color: #000000; font-size: 12pt;\">bạn c&oacute; thể di chuyển tới trung t&acirc;m th&agrave;nh phố một c&aacute;ch nhanh nhất tr&ecirc;n những con đường xanh m&aacute;t.Cư d&acirc;n chỉ mất khoảng 5 ph&uacute;t để v&agrave;o trung t&acirc;m Quận 1 v&agrave; Khu đ&ocirc; thị Ph&uacute; Mỹ Hưng, đồng thời dễ d&agrave;ng tận hưởng những tiện &iacute;ch h&agrave;ng đầu chỉ trong b&aacute;n k&iacute;nh 2km.</span></p>\r\n<p>&nbsp;<a href=\"../duan-officetel-saigon-mia-quan-7-27.html\"><img src=\"../media/images/khoang-cach.png?1470886903603\" width=\"100%\" /></a></p>\r\n<h4 style=\"text-align: center;\"><span style=\"font-size: 14pt; color: #ff0000;\"><strong>ĐĂNG K&Yacute; NGAY TH&Ocirc;NG TIN OFFICETEL MIA&nbsp;</strong></span></h4>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"47\" height=\"26\" /></strong><a href=\"../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>', '<p><span style=\"font-size: 12pt; color: #000000;\"><strong>MẶT BẰNG TỔNG QU&Aacute;T OFFICETEL SAIGON MIA</strong></span></p>\r\n<p><a href=\"../duan-officetel-saigon-mia-quan-7-27.html\"><strong><span style=\"color: #000000;\"><img src=\"../media/images/officetel-saigon.jpg?1475135319109\" width=\"100%\" /></span></strong></a></p>\r\n<h4 style=\"text-align: center;\"><span style=\"font-size: 14pt; color: #ff0000;\"><strong>ĐĂNG K&Yacute; NGAY NHẬN BẢNG GI&Aacute; OFFICETEL MIA</strong></span></h4>\r\n<h4><strong><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"http://sanhungthinh.com/upload/images/animated-arrow.gif\" alt=\"\" width=\"47\" height=\"26\" /></strong><a href=\"../duan-saigonmia-trung-son-sky-in-your-eyes-1.html#dangkyxemnha\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"../media/images/dang-ky-button.png?1466180235102\" width=\"200\" height=\"76\" /></a></h4>', '<p>sdfsdf</p>', '2', 'logo-saigon.jpg', null);
INSERT INTO `duan` VALUES ('28', 'SHOPHOUSE SKY CENTER LUXURY - QUẬN TÂN BÌNH', '<p style=\"text-align: justify;\"><strong><span style=\"color: #800000;\">SHOPHOUSE SKY CENTER</span></strong> vị tr&iacute; v&agrave;ng sinh lợi cho qu&yacute; kh&aacute;ch h&agrave;ng,hội tụ mọi yếu tố cho giới kinh doanh hướng tới.T&iacute;nh thanh khoan cực kỳ nhanh,thu hồi vốn v&agrave; sinh lời trong khoảng thời gian ngắn.</p>', '2', '2016-11-07 09:44:06', 'shophouse-sky-2center.jpg', '<p><strong><span style=\"font-size: 12pt;\">&nbsp;<span style=\"color: #800000;\"><a style=\"color: #800000;\" title=\"Shop House\" href=\"../duan-shophouse-sky-center-luxury-quan-tan-binh-28.html\">Shop House</a></span></span> <span style=\"font-size: 12pt; color: #000000;\">Block B-C-D Sky Center</span></strong></p>\r\n<p><span style=\"font-size: 12pt; color: #000000;\"><strong>Shop House căn h&ocirc;̣ Sky Center giá chỉ từ 42 tri&ecirc;̣u/m2</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">Căn H&ocirc;̣ ShopHouse bao g&ocirc;̀m 1 t&acirc;̀ng tr&ecirc;̣t làm trung t&acirc;m thương mại và 1 t&acirc;̀ng lửng đ&ecirc;̉ ở có đ&acirc;̀y đủ phòng khách, phòng b&ecirc;́p, phòng v&ecirc;̣ sinh, và 1-2 phòng ngủ</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\">Với vị trí cực kỳ trắc địa và s&ocirc;́ lượng có hạn (t&ocirc;̉ng c&ocirc;̣ng 28 căn Shop House) ShopHouse SkyCenter th&acirc;̣t sự là m&ocirc;̣t cơ h&ocirc;̣i kinh doanh, cơ h&ocirc;̣i đ&acirc;̀u tư, cơ h&ocirc;̣i khẳng định đẳng c&acirc;́p tuy&ecirc;̣t vời nh&acirc;́t tại dự án này</span></p>\r\n<p><span style=\"font-size: 12pt; color: #000000;\"><strong>1-Mặt Bằng Shop House Block B căn hộ Sky Center</strong></span></p>\r\n<h4><span style=\"font-size: 12pt; color: #000000;\"><strong>- Mặt Bằng Tầng Trệt</strong></span></h4>\r\n<p><a href=\"../duan-shophouse-sky-center-luxury-quan-tan-binh-28.html\"><img src