INSERT INTO `duan` VALUES ('29', 'SHOPHOUSE RICHMOND CITY - QUẬN BÌNH THẠNH', '<p style=\"text-align: justify;\"><strong><span style=\"color: #800000;\">SHOPHOUSE RICHMOND CITY</span></strong>&nbsp;vị tr&iacute; v&agrave;ng sinh lợi cho qu&yacute; kh&aacute;ch h&agrave;ng,hội tụ mọi yếu tố cho giới kinh doanh hướng tới.T&iacute;nh thanh khoan cực kỳ nhanh,thu hồi vốn v&agrave; sinh lời trong khoảng thời gian ngắn.</p>', '2', '2016-11-07 09:44:56', 'shophouse-richmond-1.png', '<p style=\"text-align: justify;\"><span style=\"color: #000000; font-size: 12pt;\"><strong>SHOPHOUSE RICHMOND CITY&nbsp;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #800000; font-size: 12pt;\"><strong><a style=\"color: #800000;\" href=\"../duan-shophouse-richmond-city-quan-binh-thanh-29.html\">Shophouse</a></strong></span><span style=\"color: #000000;\">&nbsp;<span style=\"font-size: 12pt;\">l&agrave; những căn mặt tiền, tầng trệt hoặc trệt+lửng, ph&ugrave; hợp cho nhu cầu kinh doanh mua b&aacute;n, v&igrave; sở hữu mặt bằng tầng trệt cho n&ecirc;n số lượng căn</span></span> <span style=\"color: #800000; font-size: 12pt;\"><strong><a style=\"color: #800000;\" href=\"../duan-shophouse-richmond-city-quan-binh-thanh-29.html\">Shophouse</a></strong></span><span style=\"font-size: 12pt;\">&nbsp;<span style=\"color: #000000;\">trong một dự &aacute;n l&agrave; kh&ocirc;ng nhiều v&agrave; v&ocirc; c&ugrave;ng qu&yacute; gi&aacute;,</span></span> <span style=\"color: #800000; font-size: 12pt;\"><strong><a style=\"color: #800000;\" href=\"../duan-shophouse-richmond-city-quan-binh-thanh-29.html\">Shophouse Richmond City</a></strong></span>&nbsp;<span style=\"font-size: 12pt;\">c<span style=\"color: #000000;\">&oacute; tổng cộng 30 căn, trong đ&oacute; Block Glory chiếm 09 căn v&agrave; Block Riches c&oacute; 21 căn.</span></span></p>\r\n<p><a href=\"../duan-shophouse-richmond-city-quan-binh-thanh-29.htmlhttp:/tapdoanhungthinh.vn/duan-shophouse-richmond-city-quan-binh-than