INSERT INTO `news` VALUES ('10', 'TOP LINH VẬT PHONG THỦY HÚT TÀI LỘC TRONG THÁNG CÔ HỒN', '<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Nếu bạn đang sở hữu một ng&ocirc;i nh&agrave; chịu ảnh hưởng xấu của kiến tr&uacute;c xung quanh hoặc của c&aacute;c phi tinh th&igrave; rất cần c&oacute; linh vật để h&oacute;a giải tai ương..Sau đ&acirc;y l&agrave; một số vật phẩm gi&uacute;p bạn c&oacute; được phong thủy tốt.</span></p>\r\n<p>&nbsp;</p>', 'VATPHONGTHUY.jpg', '<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Nếu bạn đang sở hữu một ng&ocirc;i nh&agrave; chịu ảnh hưởng xấu của kiến tr&uacute;c xung quanh hoặc của c&aacute;c phi tinh th&igrave; rất cần c&oacute; linh vật để h&oacute;a giải tai ương..</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Theo quan niệm d&acirc;n gian xưa, c&aacute;c linh vật phong thủy dưới đ&acirc;y đều c&oacute; t&aacute;c dụng thu h&uacute;t tiền t&agrave;i, mang lại may mắn v&agrave; t&agrave;i lộc cho gia chủ, trong số đ&oacute; tỳ hưu v&agrave; thiềm thừ l&agrave; hai linh vật c&oacute; t&aacute;c dụng chi&ecirc;u t&agrave;i t&aacute;c lộc &nbsp;nhiều nhất.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Tỳ hưu</strong></span></p>\r\n<p style=\"text-align: justify;\"><strong><img src=\"../media/images/VATPHONGTHUY.jpg?1474513508493\" width=\"1000\" height=\"700\" /></strong></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tỳ Hưu c&ograve;n được gọi l&agrave; Thi&ecirc;n Lộc, Tịch T&agrave;, B&aacute;ch giải, l&agrave; linh vật nổi tiếng nhất c&oacute; t&aacute;c dụng trong việc chi&ecirc;u t&agrave;i t&aacute;c lộc, đem lại may mắn về c&ocirc;ng danh sự nghiệp, trấn trạch trừ t&agrave; kh&iacute;, bảo vệ t&agrave;i sản v&agrave; sự b&igrave;nh y&ecirc;n cho ng&ocirc;i nh&agrave; của bạn. Ngo&agrave;i ra, tỳ hưu c&ograve;n c&oacute; t&aacute;c dụng h&oacute;a giải &ldquo;Ngũ ho&agrave;ng đại s&aacute;t&rdquo; &ndash; một s&aacute;t tinh trong phong thủy thường g&acirc;y điều bất lợi cho c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh về sức khỏe v&agrave; sự t&agrave;i vận.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Thiềm thừ (C&oacute;c ba ch&acirc;n)</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">C&oacute;c 3 ch&acirc;n (Thiềm Thừ) l&agrave; một linh vật c&oacute; ba ch&acirc;n, miệng ngậm đồng tiền v&agrave;ng, ch&acirc;n đạp l&ecirc;n đống tiền cổ - một biểu tượng của sự may mắn về tiền bạc. Linh vật n&agrave;y vốn được d&ugrave;ng trong phong thủy ở những khu vực tốt, gi&uacute;p tăng cường t&agrave;i lộc, sự b&igrave;nh an, v&agrave; may mắn đồng thời giảm đi những rủi ro kh&ocirc;ng tốt đến với gia đ&igrave;nh bạn.</span><br /><span style=\"color: #000000;\">&nbsp;</span><br /><span style=\"color: #000000;\">Vị tr&iacute; tốt nhất để đặt con c&oacute;c 3 ch&acirc;n ch&iacute;nh l&agrave; ở g&oacute;c đối diện ch&eacute;o với cửa ch&iacute;nh, mặt hướng v&agrave;o b&ecirc;n trong như thể c&oacute;c đang nhảy v&agrave;o trong &nbsp;nh&agrave;. C&oacute;c cũng c&oacute; thể đặt nằm b&ecirc;n dưới gầm b&agrave;n, b&ecirc;n trong tủ hoặc giấu dưới ghế, hoặc những đồ đạc kh&aacute;c&hellip;Lưu &yacute; l&agrave; kh&ocirc;ng n&ecirc;n đặt c&oacute;c đối diện thẳng với cửa ch&iacute;nh, cửa sổ.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Tượng Hổ</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tượng hổ được coi l&agrave; biểu tượng của sự quyền lực, đường c&ocirc;ng danh thăng tiến, c&oacute; thể trị được t&agrave; ma, giải hạn. Tượng hổ c&oacute; thể d&ugrave;ng tượng đ&aacute; hoặc bằng đồng.</span></p>\r\n<p><img src=\"../media/images/tuong-ho.jpg?1474513575811\" width=\"1000\" height=\"700\" /></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tượng hổ cũng n&ecirc;n đặt b&ecirc;n ngo&agrave;i vườn, hướng ra ph&iacute;a cổng hoặc đặt trước cổng c&oacute; đường chạy thẳng v&agrave;o. Tượng nhỏ th&igrave; c&oacute; thể để b&agrave;n, c&oacute; t&aacute;c dụng chế s&aacute;t, v&agrave; trợ gi&uacute;p cho việc kinh doanh được thăng tiến. Treo tranh hổ trước cửa m&agrave; c&oacute; h&agrave;nh lang chạy v&agrave;o sẽ c&oacute; t&aacute;c dụng chế s&aacute;t.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Sư tử</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Sư tử trong phong thủy c&oacute; t&aacute;c dụng trừ t&agrave; kh&iacute;, s&aacute;t kh&iacute;, ph&ugrave; trợ cho sức khỏe, t&agrave;i lộc cho ch&iacute;nh chủ ng&ocirc;i nh&agrave;. &Yacute; nghĩa của n&oacute; đối với t&agrave;i kh&iacute; ch&iacute;nh l&agrave; ở điểm c&oacute; thể b&agrave;i trừ mọi trở ngại về tiền t&agrave;i, l&agrave;m cho c&ocirc;ng việc kinh doanh được tăng trưởng nhanh. Ng&agrave;y nay, những nơi sử dụng sư tử phong thủy nhiều nhất l&agrave; ở c&aacute;c cơ quan doanh nghiệp. Thường họ sẽ đặt một đ&ocirc;i sư tử ngay trước cổng lớn đi v&agrave;o để c&oacute; thể b&agrave;i trừ v&ocirc; số c&aacute;c trở ngại g&acirc;y ra cho c&ocirc;ng việc của doanh nghiệp.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Long Quy</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Linh vật n&agrave;y l&agrave; biểu tượng quyền lực tuyệt đỉnh giữa r&ugrave;a v&agrave; l&acirc;n. Nếu trưng b&agrave;y vật phẩm n&agrave;y trong nh&agrave; sẽ c&oacute; thể h&uacute;t nhiều t&agrave;i vận gi&agrave;u c&oacute;, tăng th&ecirc;m thu nhập, th&uacute;c đẩy sự nghiệp thăng tiến, sức khỏe, tuổi thọ, bền vững v&agrave; thịnh vượng l&acirc;u d&agrave;i trong việc kinh doanh. Linh vật n&agrave;y cũng mang lại nhiều may mắn sung t&uacute;c ổn định (thay v&igrave; những yếu tố bất ngờ xảy ra, Long Quy sẽ gi&uacute;p từng bước để tiết kiệm, tăng sơ lượng tiền bạc một c&aacute;ch chắc chắn).</span></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"../media/images/long-quy_1.jpg?1474513622747\" width=\"1000\" height=\"700\" /></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Được coi l&agrave; biểu tượng của sự gi&agrave;u sang, ph&uacute; qu&yacute; v&agrave; may mắn. Theo thuật phong thủy, th&igrave; c&aacute; l&agrave; biểu tượng chiếm giữ vị tr&iacute; kh&aacute; quan trọng, đặc biệt l&agrave; c&aacute; ch&eacute;p, c&aacute; rồng v&agrave; c&aacute; v&agrave;ng (mắt lồi). C&aacute; được đại diện cho sự gi&agrave;u sang, ph&uacute; qu&yacute; bởi v&igrave; ngay trong tiếng H&aacute;n th&igrave; c&aacute; đ&atilde; mang &yacute; nghĩa của sự sung t&uacute;c dồi d&agrave;o. C&aacute;c vật phẩm h&igrave;nh c&aacute; phong thủy lu&ocirc;n được giới kinh doanh xem l&agrave; m&oacute;n đồ mang lại may mắn v&agrave; c&oacute; thể được sử dụng để đặt trong văn ph&ograve;ng hoặc tại nh&agrave;.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Tượng ch&oacute;</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Ch&oacute; l&agrave; lo&agrave;i động vật vốn rất th&ocirc;ng minh v&agrave; trung th&agrave;nh tuyệt đối với con người, Từ ng&agrave;y xưa th&igrave; con người đ&atilde; thuần được lo&agrave;i ch&oacute; hoang trở th&agrave;nh vật nu&ocirc;i v&agrave; bảo vệ tr&ocirc;ng giữ trong gia đ&igrave;nh. Ch&oacute; thường được d&ugrave;ng trong việc đi săn bắn, cũng được sử dụng trong việc chăn nu&ocirc;i gia s&uacute;c. Sức mạnh, sự nhanh nhẹn cũng như sự th&ocirc;ng minh của lo&agrave;i ch&oacute; đ&atilde; được con người điều khiển sử dụng v&agrave;o cả c&ocirc;ng việc an ninh như: truy t&igrave;m tội phạm, bảo vệ an to&agrave;n cho gia đinh.</span></p>\r\n<p>&nbsp;</p>\r\n<p><img src=\"../media/images/tuong-cho.jpg?1474513656706\" width=\"1000\" height=\"700\" /></p>\r\n<p>&nbsp;</p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Tượng ch&oacute; thường được l&agrave;m bằng đ&aacute;, Nếu được đặt ở trước cửa cổng v&agrave; hướng ra ngo&agrave;i, sẽ c&oacute; t&aacute;c dụng trấn an t&agrave; ma, mang lại nhiều điều may mắn v&agrave; tốt l&agrave;nh cho ng&ocirc;i nh&agrave;.</span></p>\r\n<p><span style=\"color: #000000;\"><strong>Ngựa</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Ngựa l&agrave; một trong những con vật rất trung th&agrave;nh với ocn người, kh&ocirc;ng những thế ngựa m&agrave; c&ograve;n l&agrave; h&igrave;nh ảnh của sự bền bỉ, t&iacute;nh ki&ecirc;n nhẫn. Đặt tượng ngựa trong nh&agrave; cũng sẽ mang lại sự may mắn cho gia chủ, l&agrave;m ăn ph&aacute;t đạt trong kinh doanh, sự nhanh nhạy v&agrave; thăng tiến nhanh trong c&ocirc;ng việc, sự nghiệp học h&agrave;nh cũng tấn tới. Tượng Ngựa rất th&iacute;ch hợp cho những người hay phải đi c&ocirc;ng t&aacute;c xa, hoặc phải b&ocirc;n ba đ&acirc;y đ&oacute;. Đ&acirc;y l&agrave; một vật phẩm kh&ocirc;ng thể thiếu được cho những doanh nh&acirc;n thường xuy&ecirc;n đi xa.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #000000;\">Bạn n&ecirc;n đặt tượng ngựa theo hướng t&acirc;y, t&acirc;y bắc, hoặc đ&ocirc;ng bắc để tăng c&aacute;t kh&iacute;. Ngo&agrave;i ra bạn cũng c&oacute; thể b&agrave;y tượng ngựa trong ph&ograve;ng kh&aacute;ch, tr&ecirc;n b&agrave;n l&agrave;m việc, b&agrave;n học, v&agrave; chỗ t&agrave;i vị trong nh&agrave;, mặt n&ecirc;n hướng ra ph&aacute;i cửa ch&iacute;nh hoặc cửa sổ sẽ rẩt đại c&aacute;t.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-shophouse-sky-center-luxury-quan-tan-binh-28.html\">CĂN THƯƠNG MẠI SHOPHOUSE SKY CENTER</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-shophouse-richmond-city-quan-binh-thanh-29.html\">CĂN THƯƠNG MẠI SHOPHOUSE RICHMOND CITY</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-shophouse-melody-au-co-quan-tan-binh-30.html\">CĂN THƯƠNG MẠI SHOPHOUSE MELODY &Acirc;U CƠ</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong>CĂN THƯƠNG MẠI SHOPHOUSE 8X PLUS</strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-can-ho-9view-hung-thinh-quan-9-2.html\">CĂN THƯƠNG MẠI SHOPHOUSE 9 VIEW</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-citizents-trung-son-khang-dinh-dang-cap-19.html\">CĂN HỘ CITIZEN.TS TRUNG SƠN</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-lavita-garden-thu-duc-phong-cach-singapor-3.html\">CĂN HỘ LAVITA GARDEN THỦ ĐỨC</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-sky-center-tan-binh-living-luxury-7.html\">CĂN HỘ SKY CENTER LUXURY T&Acirc;N B&Igrave;NH</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-can-ho-cao-cap-richmond-city-binh-thanh-tphcm-8.html\">CĂN HỘ RICHMOND CITY B&Igrave;NH THẠNH</a></strong></span></p>\r\n<p><span style=\"color: #ffffff; background-color: #993300;\"><strong><a style=\"color: #ffffff; background-color: #993300;\" href=\"../duan-can-ho-cao-cap-melody-vung-tau-20.html\">CĂN HỘ MELODY VŨNG T&Agrav