INSERT INTO `duan` VALUES ('1', 'SAIGONMIA - SKY IN YOUR EYES', '<p>sad</p>', '1', '2016-06-04 08:33:46', '0.249217001461643065saigonMia_pc.jpg', 'Liên hệ', '<p><strong>SaigonMia&nbsp;</strong>tọa lạc tại khu Trung Sơn - nơi được v&iacute; như &ldquo;b&aacute;n đảo xanh&rdquo; giữa l&ograve;ng th&agrave;nh phố, đ&acirc;y cũng l&agrave; khu vực sầm uất h&agrave;ng đầu, gắn liền với sự ph&aacute;t triển chung của khu đ&ocirc; thị Nam S&agrave;i G&ograve;n. Nhờ c&oacute; 3 mặt view s&ocirc;ng v&agrave; liền kề h&agrave;ng loạt tiện &iacute;ch hiện đại n&ecirc;n&nbsp;<strong>SaigonMia</strong>&nbsp;sẽ đem đến cho gia đ&igrave;nh bạn cuộc sống trong l&agrave;nh v&agrave; đẳng cấp nhất.</p>\r\n<p><img src=\"http://hungthinhcorp.com.vn/land/attachment/image/saigonMia_pc.jpg\" alt=\"\" /></p>\r\n<p><a href=\"http://sanhungthinh.com/can-ho-sai-gon-mia-lien-ke-him-lam-q7/\"><strong>S&agrave;i G&ograve;n Mia</strong></a>&nbsp;nằm trong quần thể kiến tr&uacute;c cao cấp v&agrave; được quy hoạch đồng bộ của khu d&acirc;n cư Trung Sơn, tọa lạc tại vị tr&iacute; đắc địa nhất của khu vực Nam S&agrave;i G&ograve;n, ngo&agrave;i việc được thừa hưởng những th&agrave;nh quả hạ tầng x&atilde; hội v&agrave; tiện t&iacute;ch&nbsp;sẵn c&oacute; của&nbsp;Mia&nbsp;Trung Sơn hứa hẹn dự &aacute;n sẽ l&agrave; nơi an cư l&yacute; tưởng mọi cư d&acirc;n S&agrave;i G&ograve;n Mia.</p>\r\n<p><strong>S&agrave;i G&ograve;n Mia sở hữu tiện &iacute;ch nội khu vượt trội&nbsp;</strong></p>\r\n<p><strong><img src=\"http://sanhungthinh.com/upload/images/Tien%20ich%202.png\" alt=\"\" /><img src=\"http://sanhungthinh.com/upload/images/Tien%20ich%203.png\" alt=\"\" /><img src=\"http://sanhungthinh.com/upload/images/Tien%20ich%204.png\" alt=\"\" /><img src=\"http://sanhungthinh.com/upload/images/Tien%20ich%205.png\" alt=\"\" /></strong><br /><br /></p>\r\n<p>&nbsp;</p>\r\n<p><strong>TỔNG QUAN DỰ &Aacute;N</strong></p>\r\n<div>&nbsp;&nbsp;&nbsp;<strong>Vị tr&iacute;:</strong>&nbsp;Đường 9A, Khu d&acirc;n cư Trung Sơn, TP. HCM&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch khu đất:</strong>&nbsp;15.126m<sup>2</sup>&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối đế:</strong>&nbsp;46,46%&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Mật độ x&acirc;y dựng khối th&aacute;p:&nbsp;</strong>33,70%&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Tổng số căn hộ:</strong>&nbsp;869 căn&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Diện t&iacute;ch căn hộ:</strong>&nbsp;49,83m<sup>2</sup>&nbsp;&ndash; 83,05m&nbsp;<sup>2</sup>&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Số block:</strong>&nbsp;03 block&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Số tầng:</strong>&nbsp;27 tầng nổi, 02 tầng hầm&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Trung t&acirc;m thương mại:</strong>&nbsp;04 tầng&nbsp;<br />&nbsp;&nbsp;&nbsp;<strong>Loại h&igrave;nh:</strong>&nbsp;Căn hộ, căn trệt thương mại, office-tel, TTTM</div>', '<p>sad</p>', '<p>sad</p>', '0');
